     1→import { storage, db } from './firebase.js';
     2→import { ref, uploadBytesResumable, getDownloadURL } from 'firebase/storage';
     3→import { doc, setDoc, getDoc, deleteDoc, serverTimestamp } from 'firebase/firestore';
     4→import { updateFolderPaths } from '../features/upload/utils/folderPathUtils.js';
     5→
     6→/**
     7→ * Upload Service for Firebase Storage with atomic metadata operations
     8→ * Handles file uploads, progress tracking, error classification, and metadata preservation
     9→ */
    10→
    11→// Upload configuration constants
    12→const UPLOAD_CONFIG = {
    13→  MAX_CONCURRENT_UPLOADS: 3,
    14→  RETRY_DELAYS: [1000, 2000, 5000, 10000], // Progressive delay in ms
    15→  MAX_RETRY_ATTEMPTS: 3,
    16→  PROGRESS_UPDATE_THROTTLE: 100, // Limit progress updates to every 100ms
    17→  CHUNK_SIZE: 256 * 1024, // 256KB chunks for resumable uploads
    18→  SESSION_STORAGE_KEY: 'bookkeeper_upload_session',
    19→};
    20→
    21→// Error classification types
    22→const ERROR_TYPES = {
    23→  NETWORK: 'network',
    24→  PERMISSION: 'permission',
    25→  STORAGE: 'storage',
    26→  FILE_SIZE: 'file_size',
    27→  SERVER: 'server',
    28→  CLIENT: 'client',
    29→  AUTHENTICATION: 'authentication',
    30→  UNKNOWN: 'unknown',
    31→};
    32→
    33→/**
    34→ * Classifies upload errors into actionable categories
    35→ */
    36→function classifyError(error) {
    37→  const errorCode = error.code || error.name || '';
    38→  const errorMessage = error.message || '';
    39→
    40→  // Network-related errors
    41→  if (
    42→    errorCode.includes('network') ||
    43→    errorCode.includes('timeout') ||
    44→    errorMessage.includes('network') ||
    45→    errorMessage.includes('offline')
    46→  ) {
    47→    return {
    48→      type: ERROR_TYPES.NETWORK,
    49→      message: 'Network connection issue. Check your internet connection and try again.',
    50→      retryable: true,
    51→      userAction: 'Check your internet connection and retry the upload.',
    52→    };
    53→  }
    54→
    55→  // Permission/Authentication errors
    56→  if (
    57→    errorCode.includes('permission') ||
    58→    errorCode.includes('unauthorized') ||
    59→    errorCode === 'auth/user-not-found'
    60→  ) {
    61→    return {
    62→      type: ERROR_TYPES.PERMISSION,
    63→      message: 'Permission denied. You may not have access to upload files.',
    64→      retryable: false,
    65→      userAction: 'Please check your account permissions or sign in again.',
    66→    };
    67→  }
    68→
    69→  // Storage-related errors
    70→  if (
    71→    errorCode.includes('storage') ||
    72→    errorCode.includes('quota') ||
    73→    errorMessage.includes('storage exceeded')
    74→  ) {
    75→    return {
    76→      type: ERROR_TYPES.STORAGE,
    77→      message: 'Storage limit exceeded. Unable to upload more files.',
    78→      retryable: false,
    79→      userAction: 'Free up storage space or upgrade your storage plan.',
    80→    };
    81→  }
    82→
    83→  // File size errors
    84→  if (
    85→    errorCode.includes('file-too-large') ||
    86→    errorMessage.includes('file size') ||
    87→    errorMessage.includes('too large')
    88→  ) {
    89→    return {
    90→      type: ERROR_TYPES.FILE_SIZE,
    91→      message: 'File is too large to upload.',
    92→      retryable: false,
    93→      userAction: 'Reduce file size or split into smaller files.',
    94→    };
    95→  }
    96→
    97→  // Server errors (Firebase)
    98→  if (
    99→    errorCode.includes('internal') ||
   100→    errorCode.includes('server') ||
   101→    errorCode.includes('unavailable')
   102→  ) {
   103→    return {
   104→      type: ERROR_TYPES.SERVER,
   105→      message: 'Server temporarily unavailable. Please try again later.',
   106→      retryable: true,
   107→      userAction: 'Wait a few minutes and retry the upload.',
   108→    };
   109→  }
   110→
   111→  // Authentication errors
   112→  if (errorCode.includes('auth') || errorCode.includes('token')) {
   113→    return {
   114→      type: ERROR_TYPES.AUTHENTICATION,
   115→      message: 'Authentication expired. Please sign in again.',
   116→      retryable: false,
   117→      userAction: 'Sign out and sign in again to refresh your session.',
   118→    };
   119→  }
   120→
   121→  // Client-side errors
   122→  if (
   123→    errorCode.includes('abort') ||
   124→    errorMessage.includes('abort') ||
   125→    errorMessage.includes('cancelled')
   126→  ) {
   127→    return {
   128→      type: ERROR_TYPES.CLIENT,
   129→      message: 'Upload was cancelled.',
   130→      retryable: true,
   131→      userAction: 'Retry the upload if it was cancelled unintentionally.',
   132→    };
   133→  }
   134→
   135→  // Default unknown error
   136→  return {
   137→    type: ERROR_TYPES.UNKNOWN,
   138→    message: `Upload failed: ${errorMessage || 'Unknown error'}`,
   139→    retryable: true,
   140→    userAction: 'Try uploading the file again. If the problem persists, contact support.',
   141→  };
   142→}
   143→
   144→/**
   145→ * Upload session management for interruption recovery
   146→ */
   147→class UploadSession {
   148→  constructor(sessionId) {
   149→    this.sessionId = sessionId || crypto.randomUUID();
   150→    this.startTime = Date.now();
   151→    this.files = [];
   152→    this.completedFiles = [];
   153→    this.failedFiles = [];
   154→    this.lastSaveTime = Date.now();
   155→  }
   156→
   157→  static fromStorage() {
   158→    try {
   159→      const stored = localStorage.getItem(UPLOAD_CONFIG.SESSION_STORAGE_KEY);
   160→      if (stored) {
   161→        const data = JSON.parse(stored);
   162→        const session = new UploadSession(data.sessionId);
   163→        Object.assign(session, data);
   164→        return session;
   165→      }
   166→    } catch (error) {
   167→      console.warn('Failed to restore upload session from storage:', error);
   168→    }
   169→    return null;
   170→  }
   171→
   172→  save() {
   173→    try {
   174→      this.lastSaveTime = Date.now();
   175→      localStorage.setItem(UPLOAD_CONFIG.SESSION_STORAGE_KEY, JSON.stringify(this));
   176→    } catch (error) {
   177→      console.warn('Failed to save upload session:', error);
   178→    }
   179→  }
   180→
   181→  clear() {
   182→    try {
   183→      localStorage.removeItem(UPLOAD_CONFIG.SESSION_STORAGE_KEY);
   184→    } catch (error) {
   185→      console.warn('Failed to clear upload session:', error);
   186→    }
   187→  }
   188→
   189→  addFile(fileInfo) {
   190→    this.files.push(fileInfo);
   191→    this.save();
   192→  }
   193→
   194→  markCompleted(fileId, result) {
   195→    this.completedFiles.push({ fileId, result, timestamp: Date.now() });
   196→    this.save();
   197→  }
   198→
   199→  markFailed(fileId, error) {
   200→    this.failedFiles.push({ fileId, error, timestamp: Date.now() });
   201→    this.save();
   202→  }
   203→}
   204→
   205→/**
   206→ * Upload speed calculator with rolling averages
   207→ */
   208→class UploadSpeedCalculator {
   209→  constructor(windowSize = 10) {
   210→    this.windowSize = windowSize;
   211→    this.dataPoints = [];
   212→    this.totalBytes = 0;
   213→    this.startTime = Date.now();
   214→  }
   215→
   216→  addDataPoint(bytesTransferred, timestamp = Date.now()) {
   217→    this.dataPoints.push({ bytes: bytesTransferred, time: timestamp });
   218→
   219→    // Keep only the most recent data points
   220→    if (this.dataPoints.length > this.windowSize) {
   221→      this.dataPoints.shift();
   222→    }
   223→
   224→    this.totalBytes = Math.max(this.totalBytes, bytesTransferred);
   225→  }
   226→
   227→  getCurrentSpeed() {
   228→    if (this.dataPoints.length < 2) return 0;
   229→
   230→    const recent = this.dataPoints.slice(-5); // Use last 5 data points
   231→    if (recent.length < 2) return 0;
   232→
   233→    const timeDiff = recent[recent.length - 1].time - recent[0].time;
   234→    const bytesDiff = recent[recent.length - 1].bytes - recent[0].bytes;
   235→
   236→    return timeDiff > 0 ? (bytesDiff / timeDiff) * 1000 : 0; // bytes per second
   237→  }
   238→
   239→  getAverageSpeed() {
   240→    if (this.dataPoints.length === 0) return 0;
   241→
   242→    const elapsed = Date.now() - this.startTime;
   243→    return elapsed > 0 ? (this.totalBytes / elapsed) * 1000 : 0; // bytes per second
   244→  }
   245→
   246→  formatSpeed(bytesPerSecond) {
   247→    if (bytesPerSecond < 1024) {
   248→      return `${bytesPerSecond.toFixed(0)} B/s`;
   249→    } else if (bytesPerSecond < 1024 * 1024) {
   250→      return `${(bytesPerSecond / 1024).toFixed(1)} KB/s`;
   251→    } else {
   252→      return `${(bytesPerSecond / (1024 * 1024)).toFixed(1)} MB/s`;
   253→    }
   254→  }
   255→
   256→  getEstimatedTimeRemaining(totalBytes, currentBytes) {
   257→    const speed = this.getCurrentSpeed();
   258→    if (speed <= 0 || currentBytes >= totalBytes) return 0;
   259→
   260→    const remainingBytes = totalBytes - currentBytes;
   261→    return Math.ceil(remainingBytes / speed); // seconds
   262→  }
   263→}
   264→
   265→/**
   266→ * Main upload service class
   267→ */
   268→export class UploadService {
   269→  constructor(teamId, userId) {
   270→    this.teamId = teamId;
   271→    this.userId = userId;
   272→    this.activeUploads = new Map(); // fileId -> upload task
   273→    this.uploadTasks = new Map(); // fileId -> Firebase upload task
   274→    this.progressCallbacks = new Map(); // fileId -> callback function
   275→    this.session = new UploadSession();
   276→    this.speedCalculator = new UploadSpeedCalculator();
   277→    this.isPaused = false;
   278→    this.isAborted = false;
   279→
   280→    // Bind methods for consistent context
   281→    this.uploadFile = this.uploadFile.bind(this);
   282→    this.pauseUploads = this.pauseUploads.bind(this);
