   302→  /**
   303→   * Checks if file content already exists in storage
   304→   */
   305→  async checkFileExists(fileHash, originalFileName) {
   306→    try {
   307→      const storagePath = this.generateStoragePath(fileHash, originalFileName);
   308→      const storageRef = ref(storage, storagePath);
   309→
   310→      // Try to get download URL - if it succeeds, file exists
   311→      await getDownloadURL(storageRef);
