   370→
   371→        // Get existing metadata to check for existing folderPaths
   372→        const metadataPath = this.generateMetadataPath(hash);
   373→        const metadataRef = doc(db, metadataPath, metadata.id);
   374→        let existingFolderPaths = '';
   375→
   376→        try {
   377→          const existingDoc = await getDoc(metadataRef);
   378→          if (existingDoc.exists()) {
   379→            existingFolderPaths = existingDoc.data().folderPaths || '';
   380→          }
