   383→            'Could not retrieve existing folderPaths, proceeding with new path only:',
   384→            error
   385→          );
   386→        }
   387→
   388→        // Update folder paths using pattern recognition
   389→        const pathUpdate = updateFolderPaths(currentFolderPath, existingFolderPaths);
   390→
   391→        const metadataDoc = {
   392→          ...metadata,
   393→          hash: hash,
   394→          downloadURL: downloadURL,
   395→          teamId: this.teamId,
   396→          userId: this.userId,
   397→          uploadedAt: serverTimestamp(),
