   418→        return {
   419→          success: true,
   420→          skipped: true,
   421→          reason: 'Exact duplicate (same metadata)',
   422→          downloadURL: null,
   423→          metadata: metadata,
   424→        };
   425→      }
   426→
   427→      // Check if file content exists in storage
   428→      const fileExists = await this.checkFileExists(hash, file.name);
   429→      let downloadURL = null;
   430→
   431→      if (!fileExists) {
   432→        // Upload file to storage
   433→        const storagePath = this.generateStoragePath(hash, file.name);
   434→        const storageRef = ref(storage, storagePath);
   435→
   436→        uploadTask = uploadBytesResumable(storageRef, file, {
   437→          customMetadata: {
   438→            teamId: this.teamId,
   439→            userId: this.userId,
   440→            originalName: file.name,
   441→            hash: hash,
   442→          },
   443→        });
   444→
   445→        // Store upload task for potential cancellation
   446→        this.uploadTasks.set(metadata.id, uploadTask);
   447→
   448→        // Set up progress tracking
   449→        let lastProgressUpdate = 0;
   450→        uploadTask.on(
   451→          'state_changed',
   452→          (snapshot) => {
   453→            if (this.isPaused || this.isAborted) {
   454→              uploadTask.pause();
   455→              return;
   456→            }
   457→
   458→            const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
   459→            const now = Date.now();
   460→
   461→            // Throttle progress updates
   462→            if (now - lastProgressUpdate > UPLOAD_CONFIG.PROGRESS_UPDATE_THROTTLE) {
   463→              this.speedCalculator.addDataPoint(snapshot.bytesTransferred, now);
   464→
   465→              if (progressCallback) {
   466→                progressCallback({
   467→                  fileId: metadata.id,
   468→                  progress: progress,
   469→                  bytesTransferred: snapshot.bytesTransferred,
   470→                  totalBytes: snapshot.totalBytes,
   471→                  speed: this.speedCalculator.getCurrentSpeed(),
   472→                  state: snapshot.state,
   473→                });
   474→              }
   475→
   476→              lastProgressUpdate = now;
   477→            }
   478→          },
   479→          (error) => {
   480→            // Upload failed - will be handled in the catch block
   481→            throw error;
   482→          }
   483→        );
   484→
   485→        // Wait for upload to complete
   486→        const snapshot = await uploadTask;
   487→        downloadURL = await getDownloadURL(snapshot.ref);
   488→      } else {
   489→        // File exists, get its download URL
   490→        const storagePath = this.generateStoragePath(hash, file.name);
   491→        const storageRef = ref(storage, storagePath);
   492→        downloadURL = await getDownloadURL(storageRef);
   493→      }
   494→
   495→      // Extract folder path from original file path
   496→      let currentFolderPath = '';
   497→      if (metadata.originalPath) {
   498→        const pathParts = metadata.originalPath.split('/');
   499→        if (pathParts.length > 1) {
   500→          currentFolderPath = pathParts.slice(0, -1).join('/');
   501→        }
   502→      }
   503→
   504→      // Get existing metadata to check for existing folderPaths
   505→      const metadataPath = this.generateMetadataPath(hash);
   506→      const metadataRef = doc(db, metadataPath, metadata.id);
   507→      let existingFolderPaths = '';
   508→
   509→      try {
   510→        const existingDoc = await getDoc(metadataRef);
   511→        if (existingDoc.exists()) {
   512→          existingFolderPaths = existingDoc.data().folderPaths || '';
   513→        }
   514→      } catch (error) {
   515→        console.warn(
   516→          'Could not retrieve existing folderPaths, proceeding with new path only:',
   517→          error
   518→        );
   519→      }
   520→
   521→      // Update folder paths using pattern recognition
   522→      const pathUpdate = updateFolderPaths(currentFolderPath, existingFolderPaths);
   523→
   524→      // Save metadata to Firestore
   525→      const metadataDoc = {
   526→        ...metadata,
   527→        hash: hash,
   528→        downloadURL: downloadURL,
   529→        teamId: this.teamId,
   530→        userId: this.userId,
   531→        uploadedAt: serverTimestamp(),
   532→        fileExists: !fileExists, // true if file was uploaded, false if it already existed
   533→        folderPaths: pathUpdate.folderPaths,
   534→      };
   535→
   536→      await setDoc(metadataRef, metadataDoc);
   537→      metadataSaved = true;
   538→
   539→      // Clean up upload task reference
   540→      this.uploadTasks.delete(metadata.id);
   541→
   542→      return {
   543→        success: true,
   544→        skipped: false,
   545→        downloadURL: downloadURL,
   546→        metadata: metadataDoc,
   547→      };
   548→    } catch (error) {
   549→      // Clean up upload task reference
   550→      this.uploadTasks.delete(metadata.id);
   551→
   552→      // If metadata was saved but upload failed, we need to clean up
   553→      // This is a rare edge case but ensures consistency
   554→      if (metadataSaved && uploadTask) {
   555→        try {
   556→          const metadataPath = this.generateMetadataPath(hash);
   557→          const metadataRef = doc(db, metadataPath, metadata.id);
   558→          await deleteDoc(metadataRef);
   559→        } catch (cleanupError) {
   560→          console.warn('Failed to clean up metadata after upload failure:', cleanupError);
   561→        }
   562→      }
   563→
   564→      const classifiedError = classifyError(error);
   565→      throw {
   566→        ...error,
   567→        classified: classifiedError,
   568→        fileId: metadata.id,
   569→        fileName: file.name,
   570→      };
   571→    }
   572→  }
