   574→  /**
   575→   * Uploads a single file with progress tracking and error handling
   576→   */
   577→  async uploadFile(fileInfo, progressCallback) {
   578→    if (this.isAborted) {
   579→      throw new Error('Upload service has been aborted');
   580→    }
   581→
   582→    // Wait if paused
   583→    while (this.isPaused && !this.isAborted) {
   584→      await new Promise((resolve) => setTimeout(resolve, 100));
   585→    }
   586→
   587→    if (this.isAborted) {
   588→      throw new Error('Upload was aborted while paused');
   589→    }
   590→
   591→    const { metadata } = fileInfo;
   592→    this.session.addFile({ id: metadata.id, name: metadata.originalName, size: metadata.size });
   593→
   594→    try {
   595→      const result = await this.uploadFileAtomic(fileInfo, progressCallback);
   596→      this.session.markCompleted(metadata.id, result);
   597→      return result;
   598→    } catch (error) {
   599→      this.session.markFailed(metadata.id, error);
   600→      throw error;
   601→    }
   602→  }
   603→
   604→  /**
   605→   * Uploads multiple files with concurrency control
   606→   */
   607→  async uploadFiles(fileInfos, options = {}) {
   608→    const {
   609→      maxConcurrent = UPLOAD_CONFIG.MAX_CONCURRENT_UPLOADS,
   610→      progressCallback,
   611→      onFileComplete,
   612→      onFileError,
   613→    } = options;
   614→
   615→    if (this.isAborted) {
   616→      throw new Error('Upload service has been aborted');
   617→    }
   618→
   619→    const results = {
   620→      successful: [],
   621→      failed: [],
   622→      skipped: [],
   623→    };
   624→
   625→    let activeUploads = 0;
   626→    let currentIndex = 0;
   627→    const promises = [];
   628→
   629→    const processNextFile = async () => {
   630→      if (currentIndex >= fileInfos.length || this.isAborted) {
   631→        return;
   632→      }
   633→
   634→      const fileInfo = fileInfos[currentIndex++];
   635→      activeUploads++;
   636→
   637→      try {
   638→        const result = await this.uploadFile(fileInfo, progressCallback);
   639→
   640→        if (result.skipped) {
   641→          results.skipped.push({ fileInfo, result });
   642→        } else {
   643→          results.successful.push({ fileInfo, result });
   644→        }
   645→
   646→        if (onFileComplete) {
   647→          onFileComplete(fileInfo, result);
   648→        }
   649→      } catch (error) {
   650→        results.failed.push({ fileInfo, error });
   651→
   652→        if (onFileError) {
   653→          onFileError(fileInfo, error);
   654→        }
   655→      } finally {
   656→        activeUploads--;
   657→
   658→        // Process next file if not at concurrency limit
   659→        if (activeUploads < maxConcurrent && currentIndex < fileInfos.length && !this.isAborted) {
   660→          promises.push(processNextFile());
   661→        }
   662→      }
   663→    };
   664→
   665→    // Start initial batch of uploads
   666→    for (let i = 0; i < Math.min(maxConcurrent, fileInfos.length); i++) {
   667→      promises.push(processNextFile());
   668→    }
   669→
   670→    // Wait for all uploads to complete
   671→    await Promise.all(promises);
   672→
   673→    return results;
   674→  }
   675→
   676→  /**
   677→   * Retry failed uploads with exponential backoff
   678→   */
   679→  async retryFailedUploads(failedFiles, options = {}) {
   680→    const {
   681→      maxRetries = UPLOAD_CONFIG.MAX_RETRY_ATTEMPTS,
   682→      progressCallback,
   683→      onFileComplete,
   684→      onFileError,
   685→    } = options;
   686→
   687→    const retryResults = {
   688→      successful: [],
   689→      failed: [],
   690→    };
   691→
   692→    for (const { fileInfo, error } of failedFiles) {
   693→      if (this.isAborted) break;
   694→
   695→      const classified = error.classified || classifyError(error);
   696→
   697→      // Skip non-retryable errors
   698→      if (!classified.retryable) {
   699→        retryResults.failed.push({ fileInfo, error });
   700→        continue;
   701→      }
   702→
   703→      let success = false;
   704→      let lastError = error;
   705→
   706→      // Attempt retries with exponential backoff
   707→      for (let attempt = 1; attempt <= maxRetries && !this.isAborted; attempt++) {
   708→        try {
   709→          // Wait before retry (exponential backoff with jitter)
   710→          const delay =
   711→            UPLOAD_CONFIG.RETRY_DELAYS[
   712→              Math.min(attempt - 1, UPLOAD_CONFIG.RETRY_DELAYS.length - 1)
   713→            ];
   714→          const jitter = Math.random() * 1000; // Add up to 1 second of jitter
   715→          await new Promise((resolve) => setTimeout(resolve, delay + jitter));
   716→
   717→          if (this.isAborted) break;
   718→
   719→          const result = await this.uploadFile(fileInfo, progressCallback);
   720→          retryResults.successful.push({ fileInfo, result });
   721→
   722→          if (onFileComplete) {
   723→            onFileComplete(fileInfo, result);
   724→          }
   725→
   726→          success = true;
   727→          break;
   728→        } catch (retryError) {
   729→          lastError = retryError;
   730→          console.warn(
   731→            `Retry attempt ${attempt} failed for file ${fileInfo.metadata.originalName}:`,
   732→            retryError
   733→          );
   734→        }
   735→      }
   736→
   737→      if (!success) {
   738→        retryResults.failed.push({ fileInfo, error: lastError });
   739→
   740→        if (onFileError) {
   741→          onFileError(fileInfo, lastError);
   742→        }
   743→      }
   744→    }
   745→
   746→    return retryResults;
   747→  }
   748→
   749→  /**
   750→   * Pause all active uploads
   751→   */
   752→  pauseUploads() {
   753→    this.isPaused = true;
   754→
   755→    // Pause all active Firebase upload tasks
   756→    for (const uploadTask of this.uploadTasks.values()) {
   757→      uploadTask.pause();
   758→    }
   759→  }
   760→
   761→  /**
   762→   * Resume all paused uploads
   763→   */
   764→  resumeUploads() {
   765→    this.isPaused = false;
   766→
   767→    // Resume all paused Firebase upload tasks
   768→    for (const uploadTask of this.uploadTasks.values()) {
   769→      uploadTask.resume();
   770→    }
   771→  }
   772→
   773→  /**
   774→   * Cancel all uploads and clean up
   775→   */
   776→  cancelUploads() {
   777→    this.isAborted = true;
   778→    this.isPaused = false;
   779→
   780→    // Cancel all active Firebase upload tasks
   781→    for (const uploadTask of this.uploadTasks.values()) {
   782→      uploadTask.cancel();
   783→    }
   784→
   785→    this.uploadTasks.clear();
   786→    this.activeUploads.clear();
   787→    this.progressCallbacks.clear();
   788→    this.session.clear();
   789→  }
   790→
   791→  /**
   792→   * Get upload statistics
   793→   */
   794→  getUploadStats() {
   795→    return {
   796→      activeUploads: this.activeUploads.size,
   797→      isPaused: this.isPaused,
   798→      isAborted: this.isAborted,
   799→      currentSpeed: this.speedCalculator.formatSpeed(this.speedCalculator.getCurrentSpeed()),
   800→      averageSpeed: this.speedCalculator.formatSpeed(this.speedCalculator.getAverageSpeed()),
   801→      sessionId: this.session.sessionId,
   802→      completedFiles: this.session.completedFiles.length,
   803→      failedFiles: this.session.failedFiles.length,
   804→    };
   805→  }
   806→
   807→  /**
   808→   * Restore upload session from localStorage
   809→   */
   810→  static restoreSession() {
   811→    return UploadSession.fromStorage();
   812→  }
   813→
   814→  /**
   815→   * Clean up resources
   816→   */
   817→  destroy() {
   818→    this.cancelUploads();
   819→  }
   820→}
   821→
   822→export default UploadService;
