   261→<script setup>
   262→import { ref, computed, onMounted, onBeforeUnmount, watch } from 'vue';
   263→import { useOrganizerStore } from '../stores/organizer.js';
   264→import { useMigration } from '../composables/useMigration.js';
   265→import TagInput from '../components/TagInput.vue';
