   315→  const ext = extension.toLowerCase();
   316→  const iconMap = {
   317→    '.pdf': 'mdi-file-pdf-box',
   318→    '.doc': 'mdi-file-word-box',
   319→    '.docx': 'mdi-file-word-box',
   320→    '.xls': 'mdi-file-excel-box',
   321→    '.xlsx': 'mdi-file-excel-box',
   322→    '.ppt': 'mdi-file-powerpoint-box',
   323→    '.pptx': 'mdi-file-powerpoint-box',
   324→    '.txt': 'mdi-file-document-outline',
   325→    '.jpg': 'mdi-file-image-outline',
   326→    '.jpeg': 'mdi-file-image-outline',
   327→    '.png': 'mdi-file-image-outline',
   328→    '.gif': 'mdi-file-image-outline',
   329→    '.zip': 'mdi-folder-zip-outline',
   330→    '.rar': 'mdi-folder-zip-outline'
   331→  };
   332→  return iconMap[ext] || 'mdi-file-outline';
   333→};
   334→
   335→const getFileIconColor = (extension) => {
   336→  const ext = extension.toLowerCase();
   337→  const colorMap = {
   338→    '.pdf': 'red-darken-1',
   339→    '.doc': 'blue-darken-1',
   340→    '.docx': 'blue-darken-1',
   341→    '.xls': 'green-darken-1',
   342→    '.xlsx': 'green-darken-1',
   343→    '.ppt': 'orange-darken-1',
   344→    '.pptx': 'orange-darken-1',
   345→    '.jpg': 'purple-darken-1',
   346→    '.jpeg': 'purple-darken-1',
