   348→    '.gif': 'purple-darken-1'
   349→  };
   350→  return colorMap[ext] || 'grey-darken-1';
   351→};
   352→
   353→const formatFileSize = (bytes) => {
   354→  if (!bytes) return '0 B';
   355→  const sizes = ['B', 'KB', 'MB', 'GB'];
   356→  const i = Math.floor(Math.log(bytes) / Math.log(1024));
   357→  return `${(bytes / Math.pow(1024, i)).toFixed(1)} ${sizes[i]}`;
   358→};
   359→
   360→const formatDate = (timestamp) => {
   361→  if (!timestamp) return '';
   362→  const date = timestamp.toDate ? timestamp.toDate() : new Date(timestamp);
   363→  return date.toLocaleDateString();
   364→};
