   400→    
   401→    const summary = migration.getSummary();
   402→    if (summary) {
   403→      showNotification(
   404→        `Migration complete: ${summary.successful} documents organized`,
   405→        'success'
   406→      );
   407→    }
   408→    
   409→    showMigrationPrompt.value = false;
