   132‚Üí<script setup>
   133‚Üíimport { computed } from 'vue';
   134‚Üíimport TagSelector from './TagSelector.vue';
   135‚Üí
   136‚Üí// Props
   137‚Üíconst props = defineProps({
   138‚Üí  // Evidence/file data
   139‚Üí  evidence: {
   140‚Üí    type: Object,
   141‚Üí    required: true,
   142‚Üí  },
   143‚Üí  
   144‚Üí  // Display options
   145‚Üí  readonly: {
   146‚Üí    type: Boolean,
   147‚Üí    default: false,
   148‚Üí  },
   149‚Üí  showActions: {
   150‚Üí    type: Boolean,
   151‚Üí    default: true,
   152‚Üí  },
   153‚Üí  showProcessingStage: {
   154‚Üí    type: Boolean,
   155‚Üí    default: false,
   156‚Üí  },
   157‚Üí  selectable: {
   158‚Üí    type: Boolean,
   159‚Üí    default: false,
   160‚Üí  },
   161‚Üí  selected: {
   162‚Üí    type: Boolean,
   163‚Üí    default: false,
   164‚Üí  },
   165‚Üí  
   166‚Üí  // Loading states
   167‚Üí  tagUpdateLoading: {
   168‚Üí    type: Boolean,
   169‚Üí    default: false,
   170‚Üí  },
   171‚Üí  actionLoading: {
   172‚Üí    type: Boolean,
   173‚Üí    default: false,
   174‚Üí  },
   175‚Üí  
   176‚Üí  // Customization
   177‚Üí  tagInputPlaceholder: {
   178‚Üí    type: String,
   179‚Üí    default: 'Add tags...',
   180‚Üí  },
   181‚Üí});
   182‚Üí
   183‚Üí// Emits
   184‚Üíconst emit = defineEmits([
   185‚Üí  'tags-update',
   186‚Üí  'tag-error',
   187‚Üí  'click',
   188‚Üí  'download',
   189‚Üí  'rename',
   190‚Üí  'view-details',
   191‚Üí  'delete',
   192‚Üí  'selection-change',
   193‚Üí]);
   194‚Üí
   195‚Üí// Computed properties
   196‚Üíconst displayName = computed(() => {
   197‚Üí  return props.evidence?.displayName || 'Unnamed file';
   198‚Üí});
   199‚Üí
   200‚Üíconst fileExtension = computed(() => {
   201‚Üí  const filename = props.evidence?.displayName || '';
   202‚Üí  return filename.includes('.')
   203‚Üí    ? '.' + filename.split('.').pop().toLowerCase()
   204‚Üí    : '';
   205‚Üí});
   206‚Üí
   207‚Üíconst tags = computed(() => {
   208‚Üí  return props.evidence?.tags || [];
   209‚Üí});
   210‚Üí
   211‚Üíconst processingStage = computed(() => {
   212‚Üí  return props.evidence?.processingStage || 'uploaded';
   213‚Üí});
   214‚Üí
   215‚Üíconst formattedFileSize = computed(() => {
   216‚Üí  const bytes = props.evidence?.fileSize || 0;
   217‚Üí  if (!bytes) return '0 B';
   218‚Üí  
   219‚Üí  const sizes = ['B', 'KB', 'MB', 'GB'];
   220‚Üí  const i = Math.floor(Math.log(bytes) / Math.log(1024));
   221‚Üí  return `${(bytes / Math.pow(1024, i)).toFixed(1)} ${sizes[i]}`;
   222‚Üí});
   223‚Üí
   224‚Üíconst formattedDate = computed(() => {
   225‚Üí  const timestamp = props.evidence?.createdAt;
   226‚Üí  if (!timestamp) return '';
   227‚Üí  
   228‚Üí  const date = timestamp.toDate ? timestamp.toDate() : new Date(timestamp);
   229‚Üí  return date.toLocaleDateString();
   230‚Üí});
   231‚Üí
   232‚Üíconst fileIcon = computed(() => {
   233‚Üí  const ext = fileExtension.value.toLowerCase();
   234‚Üí  const iconMap = {
   235‚Üí    '.pdf': 'mdi-file-pdf-box',
   236‚Üí    '.doc': 'mdi-file-word-box',
   237‚Üí    '.docx': 'mdi-file-word-box',
   238‚Üí    '.xls': 'mdi-file-excel-box',
   239‚Üí    '.xlsx': 'mdi-file-excel-box',
   240‚Üí    '.ppt': 'mdi-file-powerpoint-box',
   241‚Üí    '.pptx': 'mdi-file-powerpoint-box',
   242‚Üí    '.txt': 'mdi-file-document-outline',
   243‚Üí    '.jpg': 'mdi-file-image-outline',
   244‚Üí    '.jpeg': 'mdi-file-image-outline',
   245‚Üí    '.png': 'mdi-file-image-outline',
   246‚Üí    '.gif': 'mdi-file-image-outline',
   247‚Üí    '.zip': 'mdi-folder-zip-outline',
   248‚Üí    '.rar': 'mdi-folder-zip-outline'
   249‚Üí  };
   250‚Üí  return iconMap[ext] || 'mdi-file-outline';
   251‚Üí});
   252‚Üí
   253‚Üíconst fileIconColor = computed(() => {
   254‚Üí  const ext = fileExtension.value.toLowerCase();
   255‚Üí  const colorMap = {
   256‚Üí    '.pdf': 'red-darken-1',
   257‚Üí    '.doc': 'blue-darken-1',
   258‚Üí    '.docx': 'blue-darken-1',
   259‚Üí    '.xls': 'green-darken-1',
   260‚Üí    '.xlsx': 'green-darken-1',
   261‚Üí    '.ppt': 'orange-darken-1',
   262‚Üí    '.pptx': 'orange-darken-1',
   263‚Üí    '.jpg': 'purple-darken-1',
   264‚Üí    '.jpeg': 'purple-darken-1',
   265‚Üí    '.png': 'purple-darken-1',
   266‚Üí    '.gif': 'purple-darken-1'
   267‚Üí  };
   268‚Üí  return colorMap[ext] || 'grey-darken-1';
   269‚Üí});
   270‚Üí
   271‚Üíconst processingStageColor = computed(() => {
   272‚Üí  const stageColors = {
   273‚Üí    'uploaded': 'grey',
   274‚Üí    'splitting': 'blue',
   275‚Üí    'merging': 'orange',
   276‚Üí    'complete': 'green'
   277‚Üí  };
   278‚Üí  return stageColors[processingStage.value] || 'grey';
   279‚Üí});
   280‚Üí
   281‚Üíconst processingStageText = computed(() => {
   282‚Üí  const stageTexts = {
   283‚Üí    'uploaded': 'Uploaded',
   284‚Üí    'splitting': 'Processing',
   285‚Üí    'merging': 'Merging',
   286‚Üí    'complete': 'Complete'
   287‚Üí  };
   288‚Üí  return stageTexts[processingStage.value] || processingStage.value;
   289‚Üí});
   290‚Üí
   291‚Üí// Event handlers
   292‚Üíconst handleClick = () => {
   293‚Üí  emit('click', props.evidence);
   294‚Üí};
   295‚Üí
   296‚Üíconst handleTagsUpdate = () => {
   297‚Üí  // TagSelector handles the update internally, just emit for refresh
   298‚Üí  emit('tags-update', props.evidence.id, []);
   299‚Üí};
   300‚Üí
   301‚Üíconst handleMigrateLegacy = () => {
   302‚Üí  // TODO: Implement migration dialog
   303‚Üí  console.log('Legacy tag migration requested for:', props.evidence.id);
   304‚Üí};
   305‚Üí
   306‚Üíconst handleTagError = (error) => {
   307‚Üí  emit('tag-error', error);
   308‚Üí};
   309‚Üí
   310‚Üíconst handleDownload = () => {
   311‚Üí  emit('download', props.evidence);
   312‚Üí};
   313‚Üí
   314‚Üíconst handleRename = () => {
   315‚Üí  emit('rename', props.evidence);
   316‚Üí};
   317‚Üí
   318‚Üíconst handleViewDetails = () => {
   319‚Üí  emit('view-details', props.evidence);
   320‚Üí};
   321‚Üí
   322‚Üíconst handleDelete = () => {
   323‚Üí  emit('delete', props.evidence);
   324‚Üí};
   325‚Üí
   326‚Üíconst handleSelectionChange = (selected) => {
   327‚Üí  emit('selection-change', props.evidence.id, selected);
   328‚Üí};
   329‚Üí</script>
