    17‚Üí/**
    18‚Üí * Evidence Service - Manages evidence documents in Firestore
    19‚Üí * Handles creation, updating, and management of evidence entries that reference uploaded files
    20‚Üí */
    21‚Üíexport class EvidenceService {
    22‚Üí  constructor(teamId) {
    23‚Üí    this.teamId = teamId;
    24‚Üí  }
    25‚Üí
    26‚Üí  /**
    27‚Üí   * Create a new evidence document from uploaded file metadata
    28‚Üí   * @param {Object} uploadMetadata - Metadata from the upload system
    29‚Üí   * @param {Object} options - Additional options
    30‚Üí   * @returns {Promise<string>} - Evidence document ID
    31‚Üí   */
    32‚Üí  async createEvidenceFromUpload(uploadMetadata, options = {}) {
    33‚Üí    try {
    34‚Üí      if (!uploadMetadata.hash || !uploadMetadata.originalName) {
    35‚Üí        throw new Error('Missing required upload metadata: hash and originalName');
    36‚Üí      }
    37‚Üí
    38‚Üí      // Use the metadataHash from uploadMetadata (created during upload process)
    39‚Üí      const metadataHash = uploadMetadata.metadataHash;
    40‚Üí      
    41‚Üí      // Create evidence document with refined structure
    42‚Üí      const evidenceData = {
    43‚Üí        // Reference to actual file in Storage
    44‚Üí        storageRef: {
    45‚Üí          storage: 'uploads',
    46‚Üí          fileHash: uploadMetadata.hash
    47‚Üí        },
    48‚Üí        
    49‚Üí        // Display configuration (references specific metadata record)
    50‚Üí        displayCopy: {
    51‚Üí          metadataHash: metadataHash,
    52‚Üí          folderPath: uploadMetadata.folderPath || '/' // Default to root if no folder path
    53‚Üí        },
    54‚Üí        
    55‚Üí        // File properties (for quick access)
    56‚Üí        fileSize: uploadMetadata.size || 0,
    57‚Üí        
