      skippedDocuments: 0,
      errorDocuments: 0,
      uniqueLegacyTags: [],
      suggestedCategories: []
    };
    error.value = null;
  };

  return {
    // State
    migrationProgress,
    migrationStatus,
    migrationResults,
    error,

    // Computed
    isComplete,
    hasErrors,

    // Actions
    analyzeLegacyTags,
    performAutoMigration,
    createDefaultMappings,
    rollbackMigration,
    reset
  };
});

