   366→   * @param {string} matterId - Matter ID (defaults to 'general')
   367→   * @returns {Promise<Array<string>>} - Array of original filenames
   368→   */
   369→  async getAvailableOriginalNames(fileHash, matterId = 'general') {
   370→    try {
   371→      const originalMetadataRef = collection(db, 'teams', this.teamId, 'matters', matterId, 'originalMetadata');
   372→      const q = query(
   373→        originalMetadataRef,
   374→        where('fileHash', '==', fileHash)
   375→      );
   376→
   377→      const querySnapshot = await getDocs(q);
   378→      const originalNames = [];
   379→
   380→      querySnapshot.forEach((doc) => {
   381→        const data = doc.data();
   382→        if (data.originalName && !originalNames.includes(data.originalName)) {
   383→          originalNames.push(data.originalName);
   384→        }
   385→      });
   386→
   387→      return originalNames;
   388→    } catch (error) {
   389→      console.error('[EvidenceService] Failed to get available original names:', error);
   390→      return [];
   391→    }
   392→  }
   393→}
   394→
   395→export default EvidenceService;

