     1→<template>
     2→  <div class="file-list-container">
     3→    <!-- File list header -->
     4→    <div class="list-header">
     5→      <div class="list-title">
     6→        <h3 class="text-h6">Documents</h3>
     7→      </div>
     8→      <div class="list-controls">
     9→        <ViewModeToggle :loading="false" @view-mode-changed="handleViewModeChange" />
    10→      </div>
    11→    </div>
    12→
    13→    <!-- File display with 3 view modes -->
    14→    <div class="file-display">
    15→      <!-- List view -->
    16→      <div v-if="currentViewMode === 'list'" class="file-list">
    17→        <template v-for="(evidence, index) in props.filteredEvidence" :key="evidence.id">
    18→          <!-- Simple placeholder when not loaded -->
    19→          <div
    20→            v-if="!isItemLoaded(index)"
    21→            class="document-placeholder"
    22→            :style="{ height: '120px' }"
    23→            :data-index="index"
    24→          >
    25→            <!-- Simple loading skeleton -->
    26→            <div class="skeleton-content"></div>
    27→          </div>
    28→
    29→          <!-- Actual FileListItem when loaded -->
    30→          <FileListItem
    31→            v-else
    32→            :evidence="evidence"
    33→            :tagUpdateLoading="props.getTagUpdateLoading(evidence.id)"
    34→            :aiProcessing="props.getAIProcessing(evidence.id)"
    35→            @tags-updated="$emit('tagsUpdated')"
    36→            @download="$emit('download', $event)"
    37→            @rename="$emit('rename', $event)"
    38→            @view-details="$emit('viewDetails', $event)"
    39→            @process-with-ai="$emit('process-with-ai', $event)"
    40→          />
    41→        </template>
    42→      </div>
    43→
    44→      <!-- Grid view placeholder -->
    45→      <div v-else-if="currentViewMode === 'grid'" class="file-grid">
    46→        <p class="text-body-2 text-center text-medium-emphasis pa-8">
    47→          <v-icon size="48" class="mb-2 d-block">mdi-view-grid</v-icon>
    48→          Folder Grid coming in future updates
    49→        </p>
    50→      </div>
    51→
    52→      <!-- Tree view placeholder -->
    53→      <div v-else-if="currentViewMode === 'tree'" class="file-tree">
    54→        <p class="text-body-2 text-center text-medium-emphasis pa-8">
    55→          <v-icon size="48" class="mb-2 d-block">mdi-file-tree</v-icon>
    56→          Folder Tree coming in future updates
    57→        </p>
    58→      </div>
    59→    </div>
    60→  </div>
    61→</template>
    62→
    63→<script setup>
    64→import { ref, computed, onMounted, onUnmounted, nextTick, watch } from 'vue';
    65→import FileListItem from './FileListItem.vue';
    66→import ViewModeToggle from './ViewModeToggle.vue';
    67→import { useLazyDocuments } from '@/composables/useLazyDocuments.js';
    68→
    69→// Debug logging helper
    70→const debugLog = (message, data = null) => {
    71→  const timestamp = new Date().toISOString().substring(11, 23);
    72→  console.log(`[${timestamp}] [FileListDisplay] ${message}`, data || '');
    73→};
    74→
    75→// Props
    76→const props = defineProps({
    77→  filteredEvidence: {
    78→    type: Array,
    79→    default: () => [],
    80→  },
    81→  viewMode: {
    82→    type: String,
    83→    default: 'list',
    84→  },
    85→  getEvidenceTags: {
    86→    type: Function,
    87→    required: true,
    88→  },
    89→  getTagUpdateLoading: {
    90→    type: Function,
    91→    required: true,
    92→  },
    93→  getAIProcessing: {
    94→    type: Function,
    95→    required: true,
    96→  },
    97→});
    98→
    99→// Local state for current view mode
   100→const currentViewMode = ref('list');
   101→
   102→// Add lazy loading composable
   103→const { isItemLoaded, loadItem, preloadInitialItems } = useLazyDocuments(
   104→  computed(() => props.filteredEvidence),
   105→  { initialCount: 10 }
   106→);
   107→
   108→// Intersection Observer for lazy loading
   109→let observer = null;
   110→let renderStartTime = null;
   111→let dataLoadTime = null;
   112→
   113→// Emits
   114→const emit = defineEmits([
   115→  'update:viewMode',
   116→  'tagsUpdated',
   117→  'process-with-ai',
   118→  'download',
   119→  'rename',
   120→  'viewDetails',
   121→]);
   122→
   123→// Handle view mode changes from ViewModeToggle
   124→const handleViewModeChange = (event) => {
   125→  debugLog(`View mode changed to: ${event.mode}`);
   126→  currentViewMode.value = event.mode;
   127→  emit('update:viewMode', event.mode);
   128→};
   129→
   130→// Debug: Watch filteredEvidence changes to track data loading timing
   131→watch(() => props.filteredEvidence, (newEvidence, oldEvidence) => {
   132→  dataLoadTime = performance.now();
   133→  if (newEvidence?.length > 0) {
   134→    debugLog(`📊 Data loaded: ${newEvidence.length} documents`);
   135→  }
   136→}, { immediate: true, deep: false });
   137→
   138→// Debug: Track lazy loading state
   139→watch(currentViewMode, (newMode) => {
   140→  if (newMode === 'list') {
   141→    renderStartTime = performance.now();
   142→    
   143→    // Track render performance
   144→    nextTick(() => {
   145→      const loadedCount = props.filteredEvidence?.reduce((count, _, index) => {
   146→        return isItemLoaded(index) ? count + 1 : count;
   147→      }, 0) || 0;
   148→      
   149→      const renderTime = performance.now() - renderStartTime;
   150→      
   151→      debugLog(`⚡ Rendered ${loadedCount}/${props.filteredEvidence?.length || 0} items in ${renderTime.toFixed(1)}ms`);
   152→    });
   153→  }
   154→}, { immediate: true });
   155→
   156→// Setup lazy loading on mount
   157→onMounted(async () => {
   158→  // Initialize with first 10 items
   159→  preloadInitialItems();
   160→
   161→  // Wait for DOM updates
   162→  await nextTick();
   163→
   164→  // Setup Intersection Observer for remaining items
   165→  observer = new IntersectionObserver(
   166→    (entries) => {
   167→      const loadedItems = entries.filter(entry => entry.isIntersecting).length;
   168→      if (loadedItems > 0) {
   169→        debugLog(`👁️ Loading ${loadedItems} more items`);
   170→      }
   171→      entries.forEach((entry) => {
   172→        if (entry.isIntersecting) {
   173→          const index = parseInt(entry.target.dataset.index);
   174→          loadItem(index);
   175→          observer.unobserve(entry.target);
   176→        }
   177→      });
   178→    },
   179→    { rootMargin: '50px' }
   180→  );
   181→
   182→  // Observe all placeholder elements
   183→  const placeholders = document.querySelectorAll('.document-placeholder');
   184→  placeholders.forEach((placeholder) => {
   185→    observer.observe(placeholder);
   186→  });
   187→});
   188→
   189→// Cleanup on unmount
   190→onUnmounted(() => {
   191→  debugLog(`🧹 Component unmounting, cleaning up observer`);
   192→  if (observer) {
   193→    observer.disconnect();
   194→    observer = null;
   195→  }
   196→});
   197→</script>
   198→
   199→<style scoped>
   200→.file-list-container {
   201→  flex: 1;
   202→  padding: 24px 32px;
   203→  overflow-y: auto;
   204→}
   205→
   206→.list-header {
   207→  display: flex;
   208→  justify-content: space-between;
   209→  align-items: center;
   210→  margin-bottom: 16px;
   211→}
   212→
   213→.file-display {
   214→  min-height: 0;
   215→}
   216→
   217→.file-grid,
   218→.file-tree {
   219→  min-height: 200px;
   220→  display: flex;
   221→  align-items: center;
   222→  justify-content: center;
   223→}
   224→
   225→/* Lazy loading placeholder styles */
   226→.document-placeholder {
   227→  background: #fafafa;
   228→  border: 1px solid #e0e0e0;
   229→  border-radius: 4px;
   230→  margin-bottom: 8px;
   231→  position: relative;
   232→  overflow: hidden;
   233→}
   234→
   235→.skeleton-content {
   236→  background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
   237→  background-size: 200% 100%;
   238→  animation: skeleton-loading 1.5s infinite;
   239→  height: 100%;
   240→  border-radius: 4px;
   241→}
   242→
   243→@keyframes skeleton-loading {
   244→  0% {
   245→    background-position: 200% 0;
   246→  }
   247→  100% {
   248→    background-position: -200% 0;
   249→  }
   250→}
   251→
   252→@media (max-width: 768px) {
   253→  .file-list-container {
   254→    padding: 16px 20px;
   255→  }
   256→}
   257→</style>
   258→

