            const bestFile = chooseBestFile(allUniqueFiles)

            // Remove best file from finalFiles and add back with priority
            const bestIndex = finalFiles.findIndex(f => f === bestFile)
            if (bestIndex > -1) {
              finalFiles.splice(bestIndex, 1)
            }
            finalFiles.push(bestFile)

            // Mark others as duplicates
            allUniqueFiles.forEach(fileRef => {
              if (fileRef !== bestFile) {
                const index = finalFiles.findIndex(f => f === fileRef)
                if (index > -1) {
                  finalFiles.splice(index, 1)
                }
                fileRef.isDuplicate = true
                duplicateFiles.push(fileRef)
              }
            })
          }
        }
      }
    }

    // Step 6: Combine unique and non-duplicate files
    const allFinalFiles = [...uniqueFiles, ...finalFiles]

    // Prepare result - remove File objects since they can't be transferred back
    const readyFiles = allFinalFiles.map(fileRef => ({
      id: fileRef.id,
      originalIndex: fileRef.originalIndex,
      path: fileRef.path,
      metadata: fileRef.metadata,
      hash: fileRef.hash,
      status: 'ready'
    }))

    const duplicatesForQueue = duplicateFiles.map(fileRef => ({
      id: fileRef.id,
      originalIndex: fileRef.originalIndex,
      path: fileRef.path,
      metadata: fileRef.metadata,
      hash: fileRef.hash,
      isDuplicate: fileRef.isDuplicate,
      status: 'duplicate'
    }))

    // Send completion message
    self.postMessage({
      type: MESSAGE_TYPES.PROCESSING_COMPLETE,
      batchId,
      result: {
        readyFiles,
        duplicateFiles: duplicatesForQueue
      }
    })

  } catch (error) {
    // Send error message
    self.postMessage({
      type: MESSAGE_TYPES.ERROR,
      batchId,
      error: {
        message: error.message,
        stack: error.stack
      }
    })
  }
}

// Helper function to choose the best file based on priority rules
function chooseBestFile(fileRefs) {
  return fileRefs.sort((a, b) => {
    // Priority 1: Earliest modification date
    if (a.metadata.lastModified !== b.metadata.lastModified) {
      return a.metadata.lastModified - b.metadata.lastModified
    }

    // Priority 2: Longest folder path
    const aFolderPath = a.path.substring(0, a.path.lastIndexOf('/') + 1)
    const bFolderPath = b.path.substring(0, b.path.lastIndexOf('/') + 1)
    if (aFolderPath.length !== bFolderPath.length) {
      return bFolderPath.length - aFolderPath.length // Descending (longest first)
    }

    // Priority 3: Shortest filename
    if (a.metadata.fileName.length !== b.metadata.fileName.length) {
      return a.metadata.fileName.length - b.metadata.fileName.length
    }

    // Priority 4: Alphanumeric filename sort
    if (a.metadata.fileName !== b.metadata.fileName) {
      return a.metadata.fileName.localeCompare(b.metadata.fileName)
    }

    // Priority 5: Original selection order (stable sort)
    return a.originalIndex - b.originalIndex
  })[0]
}

// Worker message handler
self.onmessage = async function(event) {
  const { type, files, batchId, options, timestamp } = event.data

  switch (type) {
    case MESSAGE_TYPES.PROCESS_FILES:
      await processFiles(files, batchId, options)
      break

    case MESSAGE_TYPES.HEALTH_CHECK:
      // Respond to health check immediately
      self.postMessage({
        type: MESSAGE_TYPES.HEALTH_CHECK_RESPONSE,
        batchId,
        timestamp: timestamp,
        responseTime: Date.now(),
        status: 'healthy'
      })
      break

    default:
      self.postMessage({
        type: MESSAGE_TYPES.ERROR,
        batchId,
        error: {
          message: `Unknown message type: ${type}`
        }
      })
  }
}

// Export message types for use by main thread
self.MESSAGE_TYPES = MESSAGE_TYPES

import { useQueueCore } from './useQueueCore'
import { useQueueWorkers } from './useQueueWorkers' 
import { useQueueProgress } from './useQueueProgress'

export function useQueueDeduplication() {

  // Initialize component modules
  const queueCore = useQueueCore()
  const queueWorkers = useQueueWorkers()
  const queueProgress = useQueueProgress()

  // Create main thread processing function with core logic
  const processFilesMainThread = async (files, updateUploadQueue, onProgress = null, skippedFolders = []) => {
    return queueProgress.processFilesMainThread(
      files, 
      updateUploadQueue,
      queueCore.processMainThreadDeduplication,
      queueCore.processDuplicateGroups,
      onProgress,
      skippedFolders
    )
  }

  // Main processFiles function that coordinates worker vs main thread
  const processFiles = async (files, updateUploadQueue, onProgress = null) => {
    return queueProgress.processFiles(
      files,
      updateUploadQueue,
      queueWorkers.processFilesWithWorker,
      processFilesMainThread,
      onProgress
    )
  }

  return {
    // Core methods (from useQueueCore)
    generateFileHash: queueCore.generateFileHash,
    chooseBestFile: queueCore.chooseBestFile,
    getFilePath: queueCore.getFilePath,

    // Main processing methods (coordinated)
    processFiles,
    processFilesMainThread,
    forceMainThreadProcessing: (files, updateUploadQueue, onProgress = null, skippedFolders = []) => 
      queueProgress.forceMainThreadProcessing(files, updateUploadQueue, 
        (files, updateUploadQueue, onProgress) => processFilesMainThread(files, updateUploadQueue, onProgress, skippedFolders), 
        onProgress),

    // Status and management (from useQueueWorkers)
    getProcessingStatus: queueWorkers.getProcessingStatus,
    forceWorkerRestart: queueWorkers.forceWorkerRestart,
    terminateWorker: queueWorkers.terminateWorker,

    // Error handling (from useQueueProgress)
    handleProcessingError: queueProgress.handleProcessingError,

    // Worker access (for backwards compatibility)
    workerInstance: queueWorkers.workerInstance,
    workerManager: queueWorkers.workerManager
  }
}
