import { ref, watch } from 'vue'
import { analyzeFiles } from '../utils/fileAnalysis.js'
import { startProcessingTimer } from '../utils/processingTimer.js'

export function useFolderOptions() {
  // Reactive data
  const showFolderOptions = ref(false)
  const includeSubfolders = ref(true)
  const pendingFolderFiles = ref([])
  const subfolderCount = ref(0)

  // Analysis state
  const isAnalyzing = ref(false)
  const mainFolderAnalysis = ref(null)
  const allFilesAnalysis = ref(null)

  // Single preprocessing function to parse all paths once and extract all needed information
  const preprocessFileData = (files) => {
    const directories = new Map() // directory path -> depth
    const fileDepths = []
    const rootFolders = new Set()
    const preprocessedFiles = []
    let hasSubfolders = false

    files.forEach(fileData => {
      // Parse path once and extract all information
      const pathParts = fileData.path.split('/').filter(part => part !== '')
      const fileDepth = pathParts.length - 1 // Subtract 1 because last part is filename
      const isMainFolder = pathParts.length === 2 // Exactly 2 parts: folder/file (no subfolders)

      // Track root folders and subfolder detection
      rootFolders.add(pathParts[0])
      if (pathParts.length > 2) {
        hasSubfolders = true
      }

      // Collect file depth for file depth stats
      fileDepths.push(fileDepth)

      // Add unique directory paths (all levels) with their depths
      const dirParts = pathParts.slice(0, -1) // Remove filename
      for (let i = 1; i <= dirParts.length; i++) {
        const dirPath = dirParts.slice(0, i).join('/')
        const dirDepth = i // Directory depth is its level in the hierarchy
        directories.set(dirPath, dirDepth)
      }

      // Store preprocessed file data
      preprocessedFiles.push({
        ...fileData,
        pathParts,
        fileDepth,
        isMainFolder,
        dirParts
      })
    })

    // Calculate directory and file depth statistics
    const maxFileDepth = fileDepths.length > 0 ? Math.max(...fileDepths) : 0
    const avgFileDepth = fileDepths.length > 0 ? fileDepths.reduce((sum, d) => sum + d, 0) / fileDepths.length : 0

    const directoryDepths = Array.from(directories.values())
    const avgDirectoryDepth = directoryDepths.length > 0 ? directoryDepths.reduce((sum, d) => sum + d, 0) / directoryDepths.length : 0

    return {
      preprocessedFiles,
      directoryStats: {
        totalDirectoryCount: directories.size,
        maxFileDepth: maxFileDepth,
        avgFileDepth: Math.round(avgFileDepth * 10) / 10,
        avgDirectoryDepth: Math.round(avgDirectoryDepth * 10) / 10
      },
      folderStats: {
        rootFolderCount: rootFolders.size,
        hasSubfolders
      }
    }
  }

  const calculateFileSizeMetrics = (files) => {
    const fileSizes = files.map(f => f.file.size)
    const totalSizeMB = fileSizes.reduce((sum, size) => sum + size, 0) / (1024 * 1024)

    // Group files by size to find identical sizes
    const sizeGroups = new Map()
    fileSizes.forEach(size => {
      sizeGroups.set(size, (sizeGroups.get(size) || 0) + 1)
    })

    const uniqueFiles = Array.from(sizeGroups.values()).filter(count => count === 1).length
    const identicalSizeFiles = fileSizes.length - uniqueFiles
    const zeroByteFiles = fileSizes.filter(size => size === 0).length

    // Get top 5 largest files
    const sortedSizes = [...fileSizes].sort((a, b) => b - a)
    const largestFileSizesMB = sortedSizes.slice(0, 5).map(size => Math.round((size / (1024 * 1024)) * 10) / 10)

    return {
      totalSizeMB: Math.round(totalSizeMB * 10) / 10,
      uniqueFiles,
      identicalSizeFiles,
      zeroByteFiles,
      largestFileSizesMB
    }
  }

  const calculateFilenameStats = (files) => {
    const filenameLengths = files.map(f => f.path.length)
    const avgFilenameLength = filenameLengths.length > 0 
      ? Math.round((filenameLengths.reduce((sum, len) => sum + len, 0) / filenameLengths.length) * 10) / 10 
      : 0

    return { avgFilenameLength }
  }

  // Analysis function
  const analyzeFilesForOptions = async () => {
    if (pendingFolderFiles.value.length === 0) return

    isAnalyzing.value = true

    try {
      // Single preprocessing pass - parse all paths once and extract everything
      const { preprocessedFiles, directoryStats: allFilesDirectoryStats } = preprocessFileData(pendingFolderFiles.value)

      // Separate files based on preprocessed data (no more path parsing!)
      const allFiles = []
      const mainFolderFiles = []
      const mainFolderFileData = []

      preprocessedFiles.forEach(fileData => {
        allFiles.push(fileData.file)

        if (fileData.isMainFolder) {
          mainFolderFiles.push(fileData.file)
          mainFolderFileData.push(fileData)
        }
      })

      // Calculate directory stats for main folder subset using preprocessed data
      const mainFolderDirectoryStats = preprocessFileData(mainFolderFileData).directoryStats

      // Calculate other metrics using the separated file arrays
      const allFilesMetrics = calculateFileSizeMetrics(pendingFolderFiles.value)
      const filenameStats = calculateFilenameStats(pendingFolderFiles.value)

      // Analyze both sets concurrently (these will log TIME_ESTIMATION_FORMULA for each)
      const [allFilesResult, mainFolderResult] = await Promise.all([
        Promise.resolve(analyzeFiles(allFiles, allFilesDirectoryStats.totalDirectoryCount, allFilesDirectoryStats.avgDirectoryDepth, allFilesDirectoryStats.avgFileDepth)),
        Promise.resolve(analyzeFiles(mainFolderFiles, mainFolderDirectoryStats.totalDirectoryCount, mainFolderDirectoryStats.avgDirectoryDepth, mainFolderDirectoryStats.avgFileDepth))
      ])

      // Log prediction-ready data (matches Folder Upload Options modal display)
      // Using JSON.stringify to prevent browser console truncation
      const analysisData = {
        timestamp: Date.now(),

        // Core predictors for 3-phase estimation (from Modal Display)
        totalFiles: allFilesResult.totalFiles,
        duplicateCandidateCount: allFilesResult.duplicateCandidates,
        duplicateCandidatePercent: allFilesResult.estimatedDuplicationPercent,
        uniqueFilesSizeMB: allFilesResult.uniqueFilesSizeMB,
        duplicateCandidatesSizeMB: allFilesResult.duplicateCandidatesSizeMB,
        totalSizeMB: allFilesResult.totalSizeMB,
        totalDirectoryCount: allFilesResult.totalDirectoryCount,
        avgDirectoryDepth: allFilesDirectoryStats.avgDirectoryDepth,
        maxDirectoryDepth: allFilesDirectoryStats.maxDirectoryDepth,

        // Additional predictors
        uniqueFilesTotal: allFilesResult.uniqueFiles,
        maxFileDepth: allFilesDirectoryStats.maxFileDepth,
        avgFileDepth: allFilesDirectoryStats.avgFileDepth,
        avgFilenameLength: filenameStats.avgFilenameLength,
        zeroByteFiles: allFilesMetrics.zeroByteFiles,
        largestFileSizesMB: allFilesMetrics.largestFileSizesMB
      }

      console.log(`ðŸ”¬ FOLDER_ANALYSIS_DATA: ${JSON.stringify(analysisData)}`)

      allFilesAnalysis.value = allFilesResult
      mainFolderAnalysis.value = mainFolderResult


    } catch (error) {
      console.error('Error analyzing files for folder options:', error)
      // Set fallback analysis
      allFilesAnalysis.value = analyzeFiles([], 0, 0, 0)
      mainFolderAnalysis.value = analyzeFiles([], 0, 0, 0)
    } finally {
      isAnalyzing.value = false
    }
  }

  // Watch for when folder options dialog opens to trigger analysis
  watch(showFolderOptions, (newValue) => {
    if (newValue && pendingFolderFiles.value.length > 0) {
      analyzeFilesForOptions()
    }
  })

  const readDirectoryRecursive = (dirEntry) => {
    return new Promise((resolve) => {
      const files = []
      const reader = dirEntry.createReader()

      const readEntries = () => {
        reader.readEntries(async (entries) => {
          if (entries.length === 0) {
            resolve(files)
            return
          }

          for (const entry of entries) {
            if (entry.isFile) {
              const file = await new Promise((resolve) => entry.file(resolve))
              files.push({ file, path: entry.fullPath })
            } else if (entry.isDirectory) {
              const subFiles = await readDirectoryRecursive(entry)
              files.push(...subFiles)
            }
          }

          readEntries() // Continue reading
        })
      }

      readEntries()
    })
  }

  const processFolderEntry = async (dirEntry, addFilesToQueue) => {
    const files = await readDirectoryRecursive(dirEntry)

    // Use preprocessing to check for subfolders and count root folders (no duplicate parsing!)
    const { folderStats } = preprocessFileData(files)

    if (folderStats.hasSubfolders) {
      pendingFolderFiles.value = files
      subfolderCount.value = folderStats.rootFolderCount
      showFolderOptions.value = true
    } else {
      // Preserve path information when adding files to queue
      const filesWithPath = files.map(f => {
        f.file.path = f.path
        return f.file
      })
      await addFilesToQueue(filesWithPath)
    }
  }

  const processFolderFiles = (files) => {
    // Convert to our standard format
    const fileDataArray = files.map(file => ({
      file,
      path: file.webkitRelativePath
    }))

    // Use preprocessing to check for subfolders (no duplicate parsing!)
    const { folderStats } = preprocessFileData(fileDataArray)

    if (folderStats.hasSubfolders) {
      pendingFolderFiles.value = fileDataArray
      subfolderCount.value = folderStats.rootFolderCount
      showFolderOptions.value = true
    } else {
      return files
    }
  }

  // Folder options handlers
  const confirmFolderOptions = (addFilesToQueue) => {
    const processingStartTime = Date.now()
    let filesToAdd = pendingFolderFiles.value

    if (!includeSubfolders.value) {
      // Use preprocessed data to filter main folder files (no more path parsing!)
      const { preprocessedFiles } = preprocessFileData(pendingFolderFiles.value)
      filesToAdd = preprocessedFiles.filter(f => f.isMainFolder)
    }

    // Start the processing timer and log T=0
    startProcessingTimer()

    // Preserve path information when adding files to queue
    const filesWithPath = filesToAdd.map(f => {
      f.file.path = f.path
      return f.file
    })

    // Store processing start time for legacy timing (can be removed later)
    window.folderProcessingStartTime = processingStartTime

    addFilesToQueue(filesWithPath)

    showFolderOptions.value = false
    pendingFolderFiles.value = []
  }

  const cancelFolderUpload = () => {
    showFolderOptions.value = false
    pendingFolderFiles.value = []
  }

  return {
    // Reactive data
    showFolderOptions,
    includeSubfolders,
    pendingFolderFiles,
    subfolderCount,

    // Analysis state
    isAnalyzing,
    mainFolderAnalysis,
    allFilesAnalysis,

    // Methods
    readDirectoryRecursive,
    processFolderEntry,
    processFolderFiles,
    confirmFolderOptions,
    cancelFolderUpload,
    analyzeFilesForOptions
  }
}

import { useWebWorker } from './useWebWorker'
import { useWorkerManager } from './useWorkerManager'
import { createApplicationError, isRecoverableError, getRetryDelay } from '../utils/errorMessages'
import { logProcessingTime } from '../utils/processingTimer.js'

export function useQueueDeduplication() {

  // Initialize Worker Manager
  const workerManager = useWorkerManager()

  // Create dedicated deduplication worker
  const WORKER_ID = 'file-deduplication-worker'
  const workerState = workerManager.createWorker(
    WORKER_ID, 
    '../workers/fileHashWorker.js',
    {
      autoRestart: true,
      maxRestartAttempts: 3,
      enableMonitoring: true
    }
  )

  // Get worker instance for direct usage (fallback to direct worker if manager fails)
  const workerInstance = workerState?.instance || useWebWorker('../workers/fileHashWorker.js')

  // Helper function to get file path consistently
  const getFilePath = (fileRef) => {
    // Handle direct file objects
    if (fileRef instanceof File) {
      return fileRef.path || fileRef.webkitRelativePath || fileRef.name
    }
    // Handle file reference objects
    return fileRef.path || fileRef.file?.webkitRelativePath || fileRef.file?.path || fileRef.file?.name || fileRef.name
  }

  // Legacy hash generation (kept for fallback compatibility)
  const generateFileHash = async (file) => {
    const buffer = await file.arrayBuffer()
    const hashBuffer = await crypto.subtle.digest('SHA-256', buffer)
    const hashArray = Array.from(new Uint8Array(hashBuffer))
    const hash = hashArray.map(b => b.toString(16).padStart(2, '0')).join('')

    // Add size suffix for collision safety (SHA-256 + size = virtually impossible collision)
    return `${hash}_${file.size}`
  }

  // Legacy chooseBestFile (kept for fallback compatibility)
  const chooseBestFile = (fileRefs) => {
    return fileRefs.sort((a, b) => {
      // Priority 1: Earliest modification date
      if (a.metadata.lastModified !== b.metadata.lastModified) {
        return a.metadata.lastModified - b.metadata.lastModified
      }

      // Priority 2: Longest folder path
      const aFolderPath = a.path.substring(0, a.path.lastIndexOf('/') + 1)
      const bFolderPath = b.path.substring(0, b.path.lastIndexOf('/') + 1)
      if (aFolderPath.length !== bFolderPath.length) {
        return bFolderPath.length - aFolderPath.length // Descending (longest first)
      }

      // Priority 3: Shortest filename
      if (a.metadata.fileName.length !== b.metadata.fileName.length) {
        return a.metadata.fileName.length - b.metadata.fileName.length
      }

      // Priority 4: Alphanumeric filename sort
      if (a.metadata.fileName !== b.metadata.fileName) {
        return a.metadata.fileName.localeCompare(b.metadata.fileName)
      }

      // Priority 5: Original selection order (stable sort)
      return a.originalIndex - b.originalIndex
    })[0]
  }

  // Web Worker-based file processing with progress support
  const processFiles = async (files, updateUploadQueue, onProgress = null) => {
    // Check if files array is valid
    if (!files || !Array.isArray(files) || files.length === 0) {
      const error = createApplicationError('Invalid files array provided', {
        validation: true,
        fileCount: files ? files.length : 0
      })
      throw error
    }

    // Check if Web Workers are supported
    if (!workerInstance.isWorkerSupported) {
      console.warn('Web Workers not supported in this browser, falling back to main thread processing')
      return processFilesMainThread(files, updateUploadQueue, onProgress)
    }

    // Check worker manager state if available
    if (workerState) {
      const stats = workerManager.getWorkerStatistics()
      console.debug('Worker statistics:', stats)
    }

    // Initialize worker if not ready
    if (!workerInstance.isWorkerReady.value) {
      console.info('Initializing Web Worker...')
      const initialized = workerInstance.initializeWorker()
      if (!initialized) {
        console.warn('Web Worker initialization failed, falling back to main thread processing')
        return processFilesMainThread(files, updateUploadQueue, onProgress)
      }
    }

    // Check worker health before proceeding
    if (!workerInstance.isWorkerHealthy.value) {
      console.warn('Web Worker is unhealthy, attempting restart...')

      // Use worker manager for restart if available
      let restarted = false
      if (workerState) {
        restarted = await workerManager.restartWorker(WORKER_ID)
      } else {
        restarted = workerInstance.restartWorker()
      }

      if (!restarted) {
        console.warn('Web Worker restart failed, falling back to main thread processing')
        return processFilesMainThread(files, updateUploadQueue, onProgress)
      }
    }

    try {
      logProcessingTime('DEDUPLICATION_START')

      // Create mapping structure that preserves original File objects
      const fileMapping = new Map()
      const filesToProcess = files.map((file, index) => {
        // Validate individual file
        if (!(file instanceof File)) {
          const error = createApplicationError(`Invalid file at index ${index}: expected File object`, {
            validation: true,
            fileIndex: index,
            fileType: typeof file
          })
          throw error
        }

        const fileId = `file_${index}_${Date.now()}`

        // Store original File object in mapping
        fileMapping.set(fileId, file)

        // Send file data to worker (File objects are cloned via structured clone)
        // Include path separately since File.path custom property doesn't survive structured cloning
        return {
          id: fileId,
          file: file,  // File objects cloned to worker, converted to ArrayBuffer internally
          originalIndex: index,
          customPath: file.path // Pass path separately to survive worker transfer
        }
      })

      // Set up progress listener if provided
      let progressUnsubscribe = null
      if (onProgress) {
        progressUnsubscribe = workerInstance.addMessageListener('PROGRESS_UPDATE', (data) => {
          onProgress(data.progress)
        })
      }

      try {
        // Send to worker with timeout based on file count and size
        const totalSize = files.reduce((sum, file) => sum + file.size, 0)
        const estimatedTime = Math.max(30000, Math.min(300000, totalSize / 1000)) // 30s min, 5min max

        const workerResult = await workerInstance.sendMessage({
          type: 'PROCESS_FILES',
          files: filesToProcess
        }, {
          timeout: estimatedTime
        })

        // Map worker results back to original File objects
        const readyFiles = workerResult.readyFiles.map(fileRef => ({
          ...fileRef,
          file: fileMapping.get(fileRef.id),  // Restore original File object
          path: fileRef.path,  // Preserve path from worker result
          status: 'ready'
        }))

        const duplicateFiles = workerResult.duplicateFiles.map(fileRef => ({
          ...fileRef,
          file: fileMapping.get(fileRef.id),  // Restore original File object
          path: fileRef.path,  // Preserve path from worker result
          status: 'duplicate'
        }))

        // Update UI using existing API
        logProcessingTime('UI_UPDATE_START')
        await updateUploadQueue(readyFiles, duplicateFiles)

        // Return in exact same format as current API
        return { readyFiles, duplicateFiles }

      } finally {
        // Clean up progress listener
        if (progressUnsubscribe) {
          progressUnsubscribe()
        }
      }

    } catch (error) {
      const appError = createApplicationError(error, {
        source: 'worker',
        fileCount: files.length,
        processingMode: 'worker'
      })

      console.error('Web Worker processing failed:', appError)

      // Determine if we should restart the worker or just fallback
      if (isRecoverableError(appError.type)) {
        console.warn('Worker appears to be stuck, restarting...')

        // Use worker manager for restart if available
        if (workerState) {
          await workerManager.restartWorker(WORKER_ID)
        } else {
          workerInstance.restartWorker()
        }
      }

      console.info('Falling back to main thread processing...')
      return processFilesMainThread(files, updateUploadQueue, onProgress)
    }
  }

  // Legacy main thread processing (fallback implementation)
  const processFilesMainThread = async (files, updateUploadQueue, onProgress = null) => {
    logProcessingTime('DEDUPLICATION_START')

    // Track progress for main thread processing
    let processedCount = 0
    const totalFiles = files.length

    const sendProgress = () => {
      if (onProgress) {
        onProgress({
          current: processedCount,
          total: totalFiles,
          percentage: Math.round((processedCount / totalFiles) * 100),
          currentFile: processedCount < totalFiles ? files[processedCount]?.name : ''
        })
      }
    }

    // Send initial progress
    sendProgress()
    // Step 1: Group files by size to identify unique-sized files
    const sizeGroupingStartTime = Date.now()
    const fileSizeGroups = new Map() // file_size -> [file_references]

    files.forEach((file, index) => {
      const fileSize = file.size
      const fileRef = {
        file,
        originalIndex: index,
        path: getFilePath(file),
        metadata: {
          fileName: file.name,
          fileSize: file.size,
          fileType: file.type,
          lastModified: file.lastModified
        }
      }

      if (!fileSizeGroups.has(fileSize)) {
        fileSizeGroups.set(fileSize, [])
      }
      fileSizeGroups.get(fileSize).push(fileRef)
    })

    const uniqueFiles = []
    const duplicateCandidates = []

    // Step 2: Separate unique-sized files from potential duplicates
    for (const [, fileRefs] of fileSizeGroups) {
      if (fileRefs.length === 1) {
        // Unique file size - definitely not a duplicate
        uniqueFiles.push(fileRefs[0])
      } else {
        // Multiple files with same size - need hash verification
        duplicateCandidates.push(...fileRefs)
      }
    }


    // Step 3: Hash potential duplicates and group by hash
    const hashGroups = new Map() // hash_value -> [file_references]
    const hashingStartTime = Date.now()

    for (const fileRef of duplicateCandidates) {
      try {
        const hash = await generateFileHash(fileRef.file)
        fileRef.hash = hash

        if (!hashGroups.has(hash)) {
          hashGroups.set(hash, [])
        }
        hashGroups.get(hash).push(fileRef)

        // Update progress
        processedCount++
        sendProgress()

      } catch (error) {
        const appError = createApplicationError(error, {
          fileProcessing: true,
          fileName: fileRef.file.name,
          fileSize: fileRef.file.size,
          processingMode: 'fallback'
        })

        console.error(`Failed to hash file ${fileRef.file.name}:`, appError)
        // Include file anyway without hash to avoid data loss - will be handled in final processing
        processedCount++
        sendProgress()
      }
    }


    const finalFiles = []
    const duplicateFiles = []

    // Step 4: Process hash groups to identify true duplicates vs identical files selected twice
    const deduplicationLogicStartTime = Date.now()
    for (const [, fileRefs] of hashGroups) {
      if (fileRefs.length === 1) {
        // Unique hash - not a duplicate
        finalFiles.push(fileRefs[0])
      } else {
        // Multiple files with same hash - check if they're one-and-the-same or duplicate files
        const oneAndTheSameGroups = new Map() // metadata_key -> [file_references]

        fileRefs.forEach(fileRef => {
          // Create metadata signature for one-and-the-same file detection
          const metadataKey = `${fileRef.metadata.fileName}_${fileRef.metadata.fileSize}_${fileRef.metadata.lastModified}`

          if (!oneAndTheSameGroups.has(metadataKey)) {
            oneAndTheSameGroups.set(metadataKey, [])
          }
          oneAndTheSameGroups.get(metadataKey).push(fileRef)
        })

        // Step 5: Handle one-and-the-same files and duplicate files
        for (const [, oneAndTheSameFiles] of oneAndTheSameGroups) {
          if (oneAndTheSameFiles.length === 1) {
            // Unique file (different metadata from others with same hash)
            finalFiles.push(oneAndTheSameFiles[0])
          } else {
            // One-and-the-same file selected multiple times - just pick the first one
            const chosenFile = oneAndTheSameFiles[0]
            finalFiles.push(chosenFile)

            // Don't mark others as duplicates - they're the same file, just filter them out
          }
        }

        // If we have multiple distinct files with same hash (duplicate files), choose the best one
        if (oneAndTheSameGroups.size > 1) {
          const allUniqueFiles = Array.from(oneAndTheSameGroups.values()).map(group => group[0])
          if (allUniqueFiles.length > 1) {
            const bestFile = chooseBestFile(allUniqueFiles)

            // Remove best file from finalFiles and add back with priority
            const bestIndex = finalFiles.findIndex(f => f === bestFile)
            if (bestIndex > -1) {
              finalFiles.splice(bestIndex, 1)
            }
            finalFiles.push(bestFile)

            // Mark others as duplicates
            allUniqueFiles.forEach(fileRef => {
              if (fileRef !== bestFile) {
                const index = finalFiles.findIndex(f => f === fileRef)
                if (index > -1) {
                  finalFiles.splice(index, 1)
                }
                fileRef.isDuplicate = true
                duplicateFiles.push(fileRef)
              }
            })
          }
        }
      }
    }


    // Step 6: Combine unique and non-duplicate files
    const combineStartTime = Date.now()
    const allFinalFiles = [...uniqueFiles, ...finalFiles]

    // Prepare for queue
    const readyFiles = allFinalFiles.map(fileRef => ({
      ...fileRef,
      status: 'ready'
    }))

    const duplicatesForQueue = duplicateFiles.map(fileRef => ({
      ...fileRef,
      status: 'duplicate'
    }))

    // Update UI using existing API
    logProcessingTime('UI_UPDATE_START')
    await updateUploadQueue(readyFiles, duplicatesForQueue)

    // Fallback processing complete - UI update timing will be handled by useFileQueue

    // Return in exact same format as current API
    return { readyFiles, duplicatesForQueue }
  }

  // Get processing status
  const getProcessingStatus = () => {
    const workerStatus = workerInstance.getWorkerStatus()
    const managerStats = workerState ? workerManager.getWorkerStatistics() : null

    return {
      ...workerStatus,
      fallbackAvailable: true,
      recommendedMode: workerStatus.supported && workerStatus.healthy ? 'worker' : 'fallback',
      workerManager: managerStats,
      managedWorker: workerState ? {
        id: WORKER_ID,
        restartAttempts: workerState.restartAttempts,
        lastRestart: workerState.lastRestart,
        errors: workerState.errors.length,
        stats: workerState.stats
      } : null
    }
  }

  // Force worker restart
  const forceWorkerRestart = async () => {
    if (workerState) {
      return await workerManager.restartWorker(WORKER_ID)
    } else {
      return workerInstance.restartWorker()
    }
  }

  // Terminate worker (cleanup)
  const terminateWorker = () => {
    if (workerState) {
      return workerManager.terminateWorker(WORKER_ID)
    } else {
      workerInstance.terminateWorker()
      return true
    }
  }

  // Force fallback mode (for testing or troubleshooting)
  const forceMainThreadProcessing = async (files, updateUploadQueue, onProgress = null) => {
    console.info('Force fallback: Processing files on main thread')
    return processFilesMainThread(files, updateUploadQueue, onProgress)
  }

  // Enhanced error handler for UI consumption
  const handleProcessingError = (error, context = {}) => {
    const appError = createApplicationError(error, context)

    // Add retry recommendations
    appError.canRetry = isRecoverableError(appError.type, context)
    appError.retryDelay = getRetryDelay(appError.type, context.retryAttempt || 1)
    appError.canUseFallback = context.processingMode !== 'fallback'

    return appError
  }

  return {
    // Methods
    generateFileHash,
    chooseBestFile,
    processFiles,
    processFilesMainThread,
    forceMainThreadProcessing,
    getFilePath,
    getProcessingStatus,
    handleProcessingError,

    // Worker management
    workerInstance,
    workerManager,
    forceWorkerRestart,
    terminateWorker
  }
}

/**
 * File analysis utility for estimating processing time and duplication
 * Extracted from worker logic for reuse in UI components
 */

/**
 * Analyze files to provide count, size, duplication estimates, and time predictions
 * @param {File[]} files - Array of File objects to analyze
 * @param {number} totalDirectoryCount - Total number of unique directories (optional)
 * @param {number} avgDirectoryDepth - Average directory nesting depth (optional)
 * @param {number} avgFileDepth - Average file nesting depth (optional)
 * @returns {Object} Analysis results
 */
export function analyzeFiles(files, totalDirectoryCount = 0, avgDirectoryDepth = 0, avgFileDepth = 0) {
  if (!files || !Array.isArray(files) || files.length === 0) {
    return {
      totalFiles: 0,
      totalSizeMB: 0,
      uniqueFiles: 0,
      duplicateCandidates: 0,
      estimatedDuplicationPercent: 0,
      estimatedTimeMs: 0,
      estimatedTimeSeconds: 0,
      totalDirectoryCount: totalDirectoryCount
    }
  }

  // Step 1: Group files by size to identify unique-sized files
  const fileSizeGroups = new Map() // file_size -> [files]

  files.forEach((file) => {
    const fileSize = file.size

    if (!fileSizeGroups.has(fileSize)) {
      fileSizeGroups.set(fileSize, [])
    }
    fileSizeGroups.get(fileSize).push(file)
  })

  const uniqueFiles = []
  const duplicateCandidates = []

  // Step 2: Separate unique-sized files from potential duplicates
  for (const [, fileGroup] of fileSizeGroups) {
    if (fileGroup.length === 1) {
      // Unique file size - definitely not a duplicate
      uniqueFiles.push(...fileGroup)
    } else {
      // Multiple files with same size - need hash verification
      duplicateCandidates.push(...fileGroup)
    }
  }

  // Calculate total size for hash candidates
  const totalSizeForHashing = duplicateCandidates.reduce((sum, file) => sum + file.size, 0)
  const totalSizeMB = totalSizeForHashing / (1024 * 1024)
  const totalFilesSizeMB = files.reduce((sum, file) => sum + file.size, 0) / (1024 * 1024)

  // Estimate duplication percentage based on files needing hash verification
  // Use +1 buffer to avoid "less than 0%" scenarios and provide conservative estimate
  const bufferedDuplicateCandidates = duplicateCandidates.length + 1
  const bufferedTotalFiles = files.length + 1
  const estimatedDuplicationPercent = Math.round((bufferedDuplicateCandidates / bufferedTotalFiles) * 100)

  // 3-phase prediction model with enhanced optimization (Trial 5)
  // Achieves 88.4% mean accuracy with optimal model selection

  // Phase 1: File Analysis - multi-factor with directory structure
  // Formula: 172.48 + (0.657 * files) + (-88.60 * avgDepth) + (-2.05 * dirCount)
  const PHASE1_BASE_MS = 172.480
  const PHASE1_FILE_MS = 0.656570
  const PHASE1_DEPTH_MS = -88.604405
  const PHASE1_DIR_MS = -2.045097

  // Phase 2: Hash Processing - combined optimal using duplicate metrics
  // Formula: -75.22 + (5.14 * duplicateCandidates) + (0.73 * duplicateSizeMB)
  const PHASE2_BASE_MS = -75.215
  const PHASE2_CANDIDATE_MS = 5.142402
  const PHASE2_DUPSIZE_MS = 0.734205

  // Phase 3: UI Rendering - multi-factor with directory complexity
  // Formula: -218.69 + (3.44 * files) + (133.74 * avgDepth) + (1.68 * dirCount)
  const PHASE3_BASE_MS = -218.692
  const PHASE3_FILE_MS = 3.441149
  const PHASE3_DEPTH_MS = 133.740506
  const PHASE3_DIR_MS = 1.682150

  // Calculate 3-phase timing using enhanced optimized models
  const phase1Time = PHASE1_BASE_MS + (files.length * PHASE1_FILE_MS) + (avgDirectoryDepth * PHASE1_DEPTH_MS) + (totalDirectoryCount * PHASE1_DIR_MS)
  const phase2Time = PHASE2_BASE_MS + (duplicateCandidates.length * PHASE2_CANDIDATE_MS) + (totalSizeMB * PHASE2_DUPSIZE_MS)
  const phase3Time = PHASE3_BASE_MS + (files.length * PHASE3_FILE_MS) + (avgDirectoryDepth * PHASE3_DEPTH_MS) + (totalDirectoryCount * PHASE3_DIR_MS)

  const totalEstimatedTime = phase1Time + phase2Time + phase3Time


  // Calculate unique file size (files that can skip hash calculation)
  const uniqueFilesSizeMB = uniqueFiles.reduce((sum, file) => sum + file.size, 0) / (1024 * 1024)

  return {
    totalFiles: files.length,
    totalSizeMB: Math.round(totalFilesSizeMB * 10) / 10, // Round to 1 decimal
    uniqueFiles: uniqueFiles.length,
    uniqueFilesSizeMB: Math.round(uniqueFilesSizeMB * 10) / 10,
    duplicateCandidates: duplicateCandidates.length,
    duplicateCandidatesSizeMB: Math.round(totalSizeMB * 10) / 10,
    estimatedDuplicationPercent,
    estimatedTimeMs: Math.round(totalEstimatedTime),
    estimatedTimeSeconds: Math.round(totalEstimatedTime / 1000 * 10) / 10,
    totalDirectoryCount: totalDirectoryCount,
    breakdown: {
      phase1TimeMs: Math.round(phase1Time),
      phase2TimeMs: Math.round(phase2Time),
      phase3TimeMs: Math.round(phase3Time),
      phase1BaseMs: PHASE1_BASE_MS,
      phase1FileMs: PHASE1_FILE_MS,
      phase1DepthMs: PHASE1_DEPTH_MS,
      phase1DirMs: PHASE1_DIR_MS,
      phase2BaseMs: PHASE2_BASE_MS,
      phase2CandidateMs: PHASE2_CANDIDATE_MS,
      phase2DupsizeMs: PHASE2_DUPSIZE_MS,
      phase3BaseMs: PHASE3_BASE_MS,
      phase3FileMs: PHASE3_FILE_MS,
      phase3DepthMs: PHASE3_DEPTH_MS,
      phase3DirMs: PHASE3_DIR_MS,
      avgDirectoryDepth: avgDirectoryDepth,
      totalDirectoryCount: totalDirectoryCount
    }
  }
}

/**
 * Format file size in human readable format
 * @param {number} bytes - Size in bytes
 * @returns {string} Formatted size string
 */
export function formatFileSize(bytes) {
  if (bytes === 0) return '0 B'

  const k = 1024
  const sizes = ['B', 'KB', 'MB', 'GB', 'TB']
  const i = Math.floor(Math.log(bytes) / Math.log(k))

  return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`
}

/**
 * Format time duration in human readable format
 * @param {number} seconds - Duration in seconds
 * @returns {string} Formatted time string
 */
export function formatDuration(seconds) {
  if (seconds < 1) return `${Math.round(seconds * 1000)}ms`
  if (seconds < 60) return `${seconds.toFixed(1)}s`

  const minutes = Math.floor(seconds / 60)
  const remainingSeconds = Math.round(seconds % 60)

  if (remainingSeconds === 0) return `${minutes}m`
  return `${minutes}m ${remainingSeconds}s`
}
