                if (index > -1) {
                  finalFiles.splice(index, 1)
                }
                fileRef.isDuplicate = true
                duplicateFiles.push(fileRef)
              }
            })
          }
        }
      }
    }

    return { finalFiles, duplicateFiles }
  }

  return {
    // Core deduplication methods
    getFilePath,
    generateFileHash,
    chooseBestFile,
    processMainThreadDeduplication,
