```javascript
rules_version = '2';
service firebase.storage {
  match /b/{bucket}/o {
    // One rule: Team members can access their team's files
    match /teams/{teamId}/matters/{matterId}/{fileName} {
      allow read, write: if request.auth != null && 
                           request.auth.token.teamId == teamId;
    }
  }
}
