        isAtRoot: false,
        currentDepth: 1,
        hierarchy: hierarchy,
        path: [{ categoryId: 'cat-1', categoryName: 'Category 1', tagName: 'Tag1' }],
        nextCategory: null
      });

      expect(context.breadcrumbs).toHaveLength(1);
    });

    it('should reset store to initial state', () => {
