    57→export const generateMockEvidence = (count = 20, categories = null) => {
    58→  if (!categories) {
    59→    categories = generateMockCategories();
    60→  }
    61→  
    62→  const evidence = [];
    63→  
    64→  // Define tag pools for each category type
    65→  const tagPools = {
    66→    'Document Type': ['Invoice', 'Receipt', 'Contract', 'Report', 'Statement', 'Agreement'],
    67→    'Date Range': ['2024-Q1', '2024-Q2', '2024-Q3', '2024-Q4', '2023-Q4', '2025-Q1'],
    68→    'Client': ['ABC Corp', 'XYZ Ltd', 'Tech Solutions', 'Global Inc', 'Local Business'],
    69→    'Status': ['Active', 'Pending', 'Completed', 'Draft', 'Archived'],
    70→    'Priority': ['High', 'Medium', 'Low', 'Critical', 'Normal'],
    71→    'Department': ['Finance', 'HR', 'Operations', 'Marketing', 'IT', 'Legal'],
    72→    'Project': ['Project Alpha', 'Project Beta', 'Project Gamma', 'Migration', 'Upgrade'],
    73→    'Location': ['New York', 'London', 'Tokyo', 'Sydney', 'Remote'],
    74→    'Source': ['Email', 'Scan', 'Upload', 'API', 'Manual Entry'],
    75→    'Classification': ['Public', 'Internal', 'Confidential', 'Restricted']
    76→  };
    77→  
    78→  for (let i = 0; i < count; i++) {
