    86→      tags: {}
    87→    };
    88→    
    89→    // Assign realistic tag combinations for each category
    90→    categories.forEach(category => {
    91→      const availableTags = tagPools[category.categoryName] || ['Default Tag'];
    92→      
    93→      // 80% chance of having tags for this category
    94→      if (Math.random() < 0.8) {
    95→        const tagCount = Math.random() < 0.7 ? 1 : Math.random() < 0.9 ? 2 : 3; // Most docs have 1 tag, some have 2-3
    96→        const selectedTags = [];
    97→        
    98→        for (let j = 0; j < tagCount; j++) {
    99→          const tagName = availableTags[Math.floor(Math.random() * availableTags.length)];
   100→          if (!selectedTags.find(tag => tag.tagName === tagName)) {
   101→            selectedTags.push({
   102→              id: `${category.categoryId}-${tagName.toLowerCase().replace(/\s+/g, '-')}`,
   103→              tagId: `${category.categoryId}-${tagName.toLowerCase().replace(/\s+/g, '-')}`,
   104→              tagName,
   105→              categoryId: category.categoryId
   106→            });
   107→          }
   108→        }
   109→        
   110→        if (selectedTags.length > 0) {
   111→          doc.tags[category.categoryId] = selectedTags;
   112→        }
   113→      }
   114→    });
   115→    
   116→    evidence.push(doc);
   117→  }
   118→  
   119→  return evidence;
   120→};
