import { describe, it, expect, beforeEach, vi } from 'vitest';
import { setActivePinia, createPinia } from 'pinia';
import { useVirtualFolderStore } from './virtualFolderStore.js';
import { 
  generateMockCategories, 
  generateMockEvidence, 
  testScenarios,
  mockStoreStates,
  performanceUtils,
  validationUtils
} from '../../../test-utils/virtualFolderTestUtils.js';

describe('Virtual Folder Store', () => {
  let store;
  let mockCategories;
  let mockEvidence;

  beforeEach(() => {
    setActivePinia(createPinia());
    store = useVirtualFolderStore();
    mockCategories = generateMockCategories(3);
    mockEvidence = generateMockEvidence(20, mockCategories);
  });

  describe('Store Instantiation', () => {
