// Hash calculation Web Worker for parallel file processing
// This worker handles SHA-256 hash calculation for file deduplication

self.onmessage = async (event) => {
  const { fileData, fileId, chunkSize = 2 * 1024 * 1024 } = event.data // 2MB chunks by default

  try {
    // Convert file data back to ArrayBuffer for processing
    const arrayBuffer = fileData

    // For small files, process directly without chunking
    if (arrayBuffer.byteLength <= chunkSize) {
      const hashBuffer = await crypto.subtle.digest('SHA-256', arrayBuffer)
      const hashArray = Array.from(new Uint8Array(hashBuffer))
      const hash = hashArray.map(b => b.toString(16).padStart(2, '0')).join('')

      self.postMessage({
        fileId,
        hash,
        success: true,
        progress: 1
      })
      return
    }

    // For large files, process in chunks with progress updates
    const totalSize = arrayBuffer.byteLength
    const chunks = Math.ceil(totalSize / chunkSize)
    let processedBytes = 0

    // Create a hash context (we'll hash the concatenated chunk hashes for consistency)
    const chunkHashes = []

    for (let i = 0; i < chunks; i++) {
      const start = i * chunkSize
      const end = Math.min(start + chunkSize, totalSize)
      const chunk = arrayBuffer.slice(start, end)

      // Hash this chunk
      const chunkHashBuffer = await crypto.subtle.digest('SHA-256', chunk)
      const chunkHashArray = Array.from(new Uint8Array(chunkHashBuffer))
      const chunkHash = chunkHashArray.map(b => b.toString(16).padStart(2, '0')).join('')
      chunkHashes.push(chunkHash)

      processedBytes += (end - start)
      const progress = processedBytes / totalSize

      // Send progress update every 10 chunks or on last chunk
      if (i % 10 === 0 || i === chunks - 1) {
        self.postMessage({
          fileId,
          progress,
          success: true,
          isProgress: true
        })
      }

      // Yield control periodically to prevent blocking
      if (i % 5 === 0) {
        await new Promise(resolve => setTimeout(resolve, 0))
      }
    }

    // Create final hash from chunk hashes for consistency
    const combinedHashData = new TextEncoder().encode(chunkHashes.join(''))
    const finalHashBuffer = await crypto.subtle.digest('SHA-256', combinedHashData)
    const finalHashArray = Array.from(new Uint8Array(finalHashBuffer))
    const hash = finalHashArray.map(b => b.toString(16).padStart(2, '0')).join('')

    self.postMessage({
      fileId,
      hash,
      success: true,
      progress: 1
    })

  } catch (error) {
    self.postMessage({
      fileId,
      error: error.message,
      success: false
    })
  }
}

// Handle worker termination gracefully
self.onerror = (error) => {
  self.postMessage({
    error: `Worker error: ${error.message}`,
    success: false
  })
}

