    if (!teamId) {
      console.warn('No team ID available for duplicate detection')
      fileInfos.forEach(fileInfo => {
        fileInfo.duplicateResult = {
          isDuplicate: false,
          exactDuplicate: null,
          metadataDuplicate: null
        }
      })
      return
    }

    // UX: Single batch query instead of N individual queries
