  props.files.forEach(file => {
    if (!hashGroups[file.hash]) {
      hashGroups[file.hash] = []
    }
    hashGroups[file.hash].push(file)
  })

  const queueDuplicates = Object.values(hashGroups)
    .filter(group => group.length > 1)
    .flat()

  return {
    queueDuplicates,
    exactDuplicates: [],
    metadataDuplicates: []
  }
})

