// Streaming deduplication processor
const processQueuedFilesStreamingly = async () => {
  // Prevent multiple simultaneous processing
  if (isProcessingQueue.value) return

  isProcessingQueue.value = true

  // Create an AbortController for cancellation
  processingController.value = new AbortController()

  try {
    // Process files in queueing state one by one
    while (true) {
      // Check for cancellation
      if (processingController.value.signal.aborted) {
        break
      }

      // Find the next file in queueing state
      const queueingFile = uploadQueue.value.find(file => file.status === 'queueing')
      if (!queueingFile) {
        // No more files to process
        break
      }

      try {
        // Calculate hash
        queueingFile.hash = await calculateFileHash(queueingFile.file)

        // Check for cancellation after hash calculation
        if (processingController.value.signal.aborted) {
          // Reset this file back to queueing state
          queueingFile.hash = null
          break
        }

        // Check for duplicates
        const duplicateResult = await checkForDuplicates(queueingFile)

        // Check for cancellation after duplicate check
        if (processingController.value.signal.aborted) {
          // Reset this file back to queueing state
          queueingFile.hash = null
          break
        }

        // Update file status based on duplicate analysis
        if (duplicateResult.isDuplicate) {
          if (duplicateResult.exactDuplicate) {
            queueingFile.status = 'skipped'
            queueingFile.isDuplicate = true
            queueingFile.isExactDuplicate = true
            const uploadDate = duplicateResult.exactDuplicate.uploadDate?.toDate?.() || new Date(duplicateResult.exactDuplicate.uploadDate)
            const formattedDate = uploadDate.toLocaleDateString()
            queueingFile.duplicateMessage = `File previously uploaded by ${duplicateResult.exactDuplicate.uploaderName} on ${formattedDate} and will be skipped.`
          } else if (duplicateResult.metadataDuplicate) {
            queueingFile.status = 'pending'
            queueingFile.isDuplicate = true
            queueingFile.isExactDuplicate = false
            const uploadDate = duplicateResult.metadataDuplicate.uploadDate?.toDate?.() || new Date(duplicateResult.metadataDuplicate.uploadDate)
            const formattedDate = uploadDate.toLocaleDateString()
            queueingFile.duplicateMessage = `Duplicate file with different metadata was uploaded by ${duplicateResult.metadataDuplicate.uploaderName} on ${formattedDate}. Proceed with upload to enable comparison of these similar files.`
          }
        } else {
          queueingFile.status = 'pending'
        }

        // Small delay to prevent UI blocking
        await new Promise(resolve => setTimeout(resolve, 10))

      } catch (error) {
        console.error('Error processing file:', queueingFile.name, error)
        // Set file to error state
        queueingFile.status = 'error'
        queueingFile.errorMessage = 'Failed to process file'
      }
    }
  } catch (error) {
    console.error('Error in streaming processing:', error)
  } finally {
    isProcessingQueue.value = false
    processingController.value = null
  }
}

