// Streaming deduplication processor with batch processing
const processQueuedFilesStreamingly = async () => {
  // Prevent multiple simultaneous processing
  if (isProcessingQueue.value) return

  isProcessingQueue.value = true

  // Create an AbortController for cancellation
  processingController.value = new AbortController()

  const BATCH_SIZE = 20 // Process 20 files at a time

  try {
    // Process files in batches
    while (true) {
      // Check for cancellation
      if (processingController.value.signal.aborted) {
        break
      }

      // Get the next batch of files in queueing state
      const queueingFiles = uploadQueue.value
        .filter(file => file.status === 'queueing')
        .slice(0, BATCH_SIZE)

      if (queueingFiles.length === 0) {
        // No more files to process
        break
      }

      try {
        // Process the entire batch in parallel
        await Promise.all(queueingFiles.map(async (file) => {
          try {
            // Check for cancellation before processing each file
            if (processingController.value.signal.aborted) {
              return
            }

            // Calculate hash
            file.hash = await calculateFileHash(file.file)

            // Check for cancellation after hash calculation
            if (processingController.value.signal.aborted) {
              // Reset this file back to queueing state
              file.hash = null
              return
            }

            // Check for duplicates
            const duplicateResult = await checkForDuplicates(file)

            // Check for cancellation after duplicate check
            if (processingController.value.signal.aborted) {
              // Reset this file back to queueing state
              file.hash = null
              return
            }

            // Update file status based on duplicate analysis
            if (duplicateResult.isDuplicate) {
              if (duplicateResult.exactDuplicate) {
                file.status = 'skipped'
                file.isDuplicate = true
                file.isExactDuplicate = true
                const uploadDate = duplicateResult.exactDuplicate.uploadDate?.toDate?.() || new Date(duplicateResult.exactDuplicate.uploadDate)
                const formattedDate = uploadDate.toLocaleDateString()
                file.duplicateMessage = `File previously uploaded by ${duplicateResult.exactDuplicate.uploaderName} on ${formattedDate} and will be skipped.`
              } else if (duplicateResult.metadataDuplicate) {
                file.status = 'pending'
                file.isDuplicate = true
                file.isExactDuplicate = false
                const uploadDate = duplicateResult.metadataDuplicate.uploadDate?.toDate?.() || new Date(duplicateResult.metadataDuplicate.uploadDate)
                const formattedDate = uploadDate.toLocaleDateString()
                file.duplicateMessage = `Duplicate file with different metadata was uploaded by ${duplicateResult.metadataDuplicate.uploaderName} on ${formattedDate}. Proceed with upload to enable comparison of these similar files.`
              }
            } else {
              file.status = 'pending'
            }

          } catch (error) {
            console.error('Error processing file:', file.name, error)
            // Set file to error state
            file.status = 'error'
            file.errorMessage = 'Failed to process file'
          }
        }))

        // Small delay between batches to prevent UI blocking
        await new Promise(resolve => setTimeout(resolve, 50))

      } catch (error) {
        console.error('Error processing batch:', error)
      }
    }
  } catch (error) {
    console.error('Error in streaming processing:', error)
  } finally {
    isProcessingQueue.value = false
    processingController.value = null
  }
}

