     1â†’import { doc, getDoc, updateDoc, serverTimestamp } from 'firebase/firestore';
     2â†’import { ref, getDownloadURL, getBytes } from 'firebase/storage';
     3â†’import { getGenerativeModel } from 'firebase/ai';
     4â†’import { db, storage, firebaseAI } from '../../../services/firebase.js';
     5â†’import { useAuthStore } from '../../../core/stores/auth.js';
     6â†’import { useCategoryStore } from '../stores/categoryStore.js';
     7â†’
     8â†’/**
     9â†’ * AI Tag Service - Handles AI-powered document categorization using Firebase Vertex AI
    10â†’ * Provides single document processing with manual triggers and approval workflow
    11â†’ */
    12â†’export class AITagService {
    13â†’  constructor(teamId = null) {
    14â†’    this.teamId = teamId;
    15â†’    this.maxFileSizeMB = parseInt(import.meta.env.VITE_AI_MAX_FILE_SIZE_MB || '20');
    16â†’  }
    17â†’
    18â†’  /**
    19â†’   * Check if AI features are enabled and properly configured
    20â†’   * @returns {boolean} - True if AI features are available
    21â†’   */
    22â†’  isAIEnabled() {
    23â†’    return (
    24â†’      import.meta.env.VITE_ENABLE_AI_FEATURES === 'true' &&
    25â†’      firebaseAI !== null
    26â†’    );
    27â†’  }
    28â†’
    29â†’  /**
    30â†’   * Get the current team ID from auth store if not provided in constructor
    31â†’   * @returns {string} - Current team ID
    32â†’   */
    33â†’  getTeamId() {
    34â†’    if (this.teamId) {
    35â†’      return this.teamId;
    36â†’    }
    37â†’    
    38â†’    const authStore = useAuthStore();
    39â†’    if (!authStore.isAuthenticated) {
    40â†’      throw new Error('User not authenticated');
    41â†’    }
    42â†’    
    43â†’    return authStore.currentTeam;
    44â†’  }
    45â†’
    46â†’  /**
    47â†’   * Process a single document with AI to suggest tags within existing categories
    48â†’   * @param {string} evidenceId - Evidence document ID
    49â†’   * @returns {Promise<Object>} - Processing result with suggested tags
    50â†’   */
    51â†’  async processSingleDocument(evidenceId) {
    52â†’    try {
    53â†’      if (!this.isAIEnabled()) {
    54â†’        throw new Error('AI features are not enabled or properly configured');
    55â†’      }
    56â†’
    57â†’      const teamId = this.getTeamId();
    58â†’      
    59â†’      // Get evidence document
    60â†’      const evidence = await this.getEvidenceDocument(evidenceId, teamId);
    61â†’      if (!evidence) {
    62â†’        throw new Error(`Evidence document not found: ${evidenceId}`);
    63â†’      }
    64â†’
    65â†’      // Validate file size
    66â†’      const fileSizeMB = (evidence.fileSize || 0) / (1024 * 1024);
    67â†’      if (fileSizeMB > this.maxFileSizeMB) {
    68â†’        throw new Error(`File size (${fileSizeMB.toFixed(1)}MB) exceeds maximum allowed (${this.maxFileSizeMB}MB)`);
    69â†’      }
    70â†’
    71â†’      // Get file content from Firebase Storage
    72â†’      const fileContent = await this.getFileForProcessing(evidence);
    73â†’      
    74â†’      // Get user's categories for AI context
    75â†’      const categories = await this.getUserCategories(teamId);
    76â†’      if (categories.length === 0) {
    77â†’        throw new Error('No categories found. Please create categories before using AI tagging.');
    78â†’      }
    79â†’
    80â†’      // Generate AI suggestions (pass file extension as fallback)
    81â†’      const displayName = evidence.displayName || '';
    82â†’      const extension = displayName.split('.').pop()?.toLowerCase() || 'pdf';
    83â†’      const aiSuggestions = await this.generateTagSuggestions(fileContent, categories, evidence, extension);
    84â†’
    85â†’      // Store AI suggestions in evidence document
    86â†’      await this.storeAISuggestions(evidenceId, teamId, aiSuggestions);
    87â†’
    88â†’      console.log(`[AITagService] Processed document ${evidenceId}: ${aiSuggestions.length} suggestions`);
    89â†’
    90â†’      return {
    91â†’        success: true,
    92â†’        evidenceId,
    93â†’        suggestedTags: aiSuggestions,
    94â†’        processedAt: new Date(),
    95â†’        categories: categories.length
    96â†’      };
    97â†’
    98â†’    } catch (error) {
    99â†’      console.error('[AITagService] Failed to process document:', error);
   100â†’      throw error;
   101â†’    }
   102â†’  }
   103â†’
   104â†’  /**
   105â†’   * Get evidence document from Firestore
   106â†’   * @param {string} evidenceId - Evidence document ID
   107â†’   * @param {string} teamId - Team ID
   108â†’   * @returns {Promise<Object|null>} - Evidence document or null
   109â†’   */
   110â†’  async getEvidenceDocument(evidenceId, teamId) {
   111â†’    try {
   112â†’      const evidenceRef = doc(db, 'teams', teamId, 'evidence', evidenceId);
   113â†’      const evidenceSnap = await getDoc(evidenceRef);
   114â†’      
   115â†’      if (evidenceSnap.exists()) {
   116â†’        return {
   117â†’          id: evidenceSnap.id,
   118â†’          ...evidenceSnap.data()
   119â†’        };
   120â†’      }
   121â†’      
   122â†’      return null;
   123â†’    } catch (error) {
   124â†’      console.error('[AITagService] Failed to get evidence document:', error);
   125â†’      throw error;
   126â†’    }
   127â†’  }
   128â†’
   129â†’  /**
   130â†’   * Retrieve file content from Firebase Storage for AI processing
   131â†’   * @param {Object} evidence - Evidence document
   132â†’   * @returns {Promise<string>} - Base64 encoded file content for AI processing
   133â†’   */
   134â†’  async getFileForProcessing(evidence) {
   135â†’    try {
   136â†’      if (!evidence.storageRef?.fileHash) {
   137â†’        throw new Error('No file hash found in evidence document');
   138â†’      }
   139â†’
   140â†’      // Get file extension from displayName
   141â†’      const displayName = evidence.displayName || '';
   142â†’      const extension = displayName.split('.').pop()?.toLowerCase() || 'pdf';
   143â†’
   144â†’      // Get file from actual storage path: teams/{teamId}/matters/{matterId}/uploads/{fileHash}.{ext}
   145â†’      const teamId = this.getTeamId();
   146â†’      
   147â†’      // For now, assume files are in the 'general' matter
   148â†’      // TODO: Extract actual matter ID from evidence if available
   149â†’      const matterId = 'general';
   150â†’      
   151â†’      // Use the actual storage path format with extension
   152â†’      const storagePath = `teams/${teamId}/matters/${matterId}/uploads/${evidence.storageRef.fileHash}.${extension}`;
   153â†’      const fileRef = ref(storage, storagePath);
   154â†’      
   155â†’      // Get file bytes directly from Firebase Storage
   156â†’      const arrayBuffer = await getBytes(fileRef);
   157â†’      
   158â†’      // Convert ArrayBuffer to base64 efficiently (avoid stack overflow for large files)
   159â†’      const uint8Array = new Uint8Array(arrayBuffer);
   160â†’      let binaryString = '';
   161â†’      for (let i = 0; i < uint8Array.length; i++) {
   162â†’        binaryString += String.fromCharCode(uint8Array[i]);
   163â†’      }
   164â†’      const base64Data = btoa(binaryString);
   165â†’      
   166â†’      console.log(`[AITagService] Retrieved file content from: ${storagePath}`);
   167â†’      return base64Data;
   168â†’    } catch (error) {
   169â†’      console.error('[AITagService] Failed to get file for processing:', error);
   170â†’      throw error;
   171â†’    }
   172â†’  }
   173â†’
   174â†’  /**
   175â†’   * Get user's categories to provide context for AI suggestions
   176â†’   * @param {string} teamId - Team ID
   177â†’   * @returns {Promise<Array>} - Array of category objects
   178â†’   */
   179â†’  async getUserCategories(teamId) {
   180â†’    try {
   181â†’      const categoryStore = useCategoryStore();
   182â†’      
   183â†’      // Initialize categories if not loaded
   184â†’      if (!categoryStore.isInitialized) {
   185â†’        await categoryStore.loadCategories();
   186â†’      }
   187â†’
   188â†’      return categoryStore.categories.map(category => ({
   189â†’        id: category.id,
   190â†’        name: category.name,
   191â†’        color: category.color,
   192â†’        tags: category.tags || []
   193â†’      }));
   194â†’    } catch (error) {
   195â†’      console.error('[AITagService] Failed to get user categories:', error);
   196â†’      throw error;
   197â†’    }
   198â†’  }
   199â†’
   200â†’  /**
   201â†’   * Generate tag suggestions using Firebase Vertex AI
   202â†’   * @param {string} base64Data - Base64 encoded file content
   203â†’   * @param {Array} categories - User's categories
   204â†’   * @param {Object} evidence - Evidence document
   205â†’   * @param {string} extension - File extension as fallback for MIME type
   206â†’   * @returns {Promise<Array>} - Array of suggested tags
   207â†’   */
   208â†’  async generateTagSuggestions(base64Data, categories, evidence, extension = 'pdf') {
   209â†’    try {
   210â†’      if (!firebaseAI) {
   211â†’        throw new Error('Firebase AI Logic not initialized');
   212â†’      }
   213â†’
   214â†’      // Get Gemini model
   215â†’      const model = getGenerativeModel(firebaseAI, { model: 'gemini-1.5-flash' });
   216â†’
   217â†’      console.log('[AITagService] Using full document content analysis');
   218â†’
   219â†’      // Build category context for AI
   220â†’      const categoryContext = categories.map(cat => {
   221â†’        const tagList = cat.tags.map(tag => tag.name).join(', ');
   222â†’        return `Category "${cat.name}": ${tagList || 'No existing tags'}`;
   223â†’      }).join('\n');
   224â†’
   225â†’      // Create AI prompt
   226â†’      const prompt = `
   227â†’You are a document categorization assistant. Analyze the provided document and suggest appropriate tags from the existing category structure.
   228â†’
   229â†’Available Categories and Tags:
   230â†’${categoryContext}
   231â†’
   232â†’Document Information:
   233â†’- Filename: ${evidence.displayName || 'Unknown'}
   234â†’- File Size: ${((evidence.fileSize || 0) / 1024).toFixed(1)} KB
   235â†’
   236â†’Instructions:
   237â†’1. Analyze the document content carefully
   238â†’2. ONLY suggest tags that exist within the provided categories
   239â†’3. If a category has no existing tags, you may suggest new tag names that fit that category
   240â†’4. Return suggestions as JSON array in this format:
   241â†’[
   242â†’  {
   243â†’    "categoryId": "category-id",
   244â†’    "categoryName": "Category Name",
   245â†’    "tagName": "Suggested Tag",
   246â†’    "confidence": 0.85,
   247â†’    "reasoning": "Brief explanation"
   248â†’  }
   249â†’]
   250â†’5. Limit to maximum 5 suggestions
   251â†’6. Only suggest tags with confidence > 0.7
   252â†’
   253â†’Please analyze the document and provide tag suggestions:
   254â†’`;
   255â†’
   256â†’      // Get MIME type with fallback to passed extension
   257â†’      let mimeType = this.getMimeType(evidence.displayName);
   258â†’      if (mimeType === 'application/octet-stream') {
   259â†’        // Use passed extension as fallback
   260â†’        mimeType = this.getMimeType(`dummy.${extension}`);
   261â†’      }
   262â†’      console.log(`[AITagService] File: ${evidence.displayName}, Extension: ${extension}, MIME type: ${mimeType}`);
   263â†’      
   264â†’      // Generate AI response using inline data (full content analysis)
   265â†’      const result = await model.generateContent([
   266â†’        { text: prompt },
   267â†’        { inlineData: { mimeType: mimeType, data: base64Data } }
   268â†’      ]);
   269â†’
   270â†’      const response = await result.response;
   271â†’      const text = response.text();
   272â†’
   273â†’      // Parse AI response
   274â†’      return this.parseAIResponse(text, categories);
   275â†’
   276â†’    } catch (error) {
   277â†’      console.error('[AITagService] Failed to generate AI suggestions:', error);
   278â†’      throw error;
   279â†’    }
   280â†’  }
   281â†’
   282â†’  /**
   283â†’   * Parse AI response and validate suggestions
   284â†’   * @param {string} aiResponse - AI response text
   285â†’   * @param {Array} categories - Available categories
   286â†’   * @returns {Array} - Validated suggested tags
   287â†’   */
   288â†’  parseAIResponse(aiResponse, categories) {
   289â†’    try {
   290â†’      // Extract JSON from response
   291â†’      const jsonMatch = aiResponse.match(/\[[\s\S]*\]/);
   292â†’      if (!jsonMatch) {
   293â†’        throw new Error('No valid JSON found in AI response');
   294â†’      }
   295â†’
   296â†’      const suggestions = JSON.parse(jsonMatch[0]);
   297â†’      const validatedSuggestions = [];
   298â†’
   299â†’      for (const suggestion of suggestions) {
   300â†’        // Validate suggestion structure
   301â†’        if (!suggestion.categoryName || !suggestion.tagName) {
   302â†’          continue;
   303â†’        }
   304â†’
   305â†’        // Find matching category
   306â†’        const category = categories.find(cat => 
   307â†’          cat.name.toLowerCase() === suggestion.categoryName.toLowerCase()
   308â†’        );
   309â†’
   310â†’        if (!category) {
   311â†’          continue;
   312â†’        }
   313â†’
   314â†’        // Create validated suggestion with proper tagId
   315â†’        validatedSuggestions.push({
   316â†’          categoryId: category.id,
   317â†’          categoryName: category.name,
   318â†’          tagId: `ai-tag-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`, // Generate unique ID
   319â†’          tagName: suggestion.tagName.trim(),
   320â†’          color: category.color,
   321â†’          confidence: Math.min(suggestion.confidence || 0.8, 1.0),
   322â†’          reasoning: suggestion.reasoning || 'AI suggested',
   323â†’          suggestedAt: new Date(),
   324â†’          status: 'suggested'
   325â†’        });
   326â†’      }
   327â†’
   328â†’      return validatedSuggestions.slice(0, 5); // Limit to 5 suggestions
   329â†’    } catch (error) {
   330â†’      console.error('[AITagService] Failed to parse AI response:', error);
   331â†’      return [];
   332â†’    }
   333â†’  }
   334â†’
   335â†’  /**
   336â†’   * Store AI suggestions in evidence document
   337â†’   * @param {string} evidenceId - Evidence document ID
   338â†’   * @param {string} teamId - Team ID
   339â†’   * @param {Array} suggestions - AI tag suggestions
   340â†’   * @returns {Promise<void>}
   341â†’   */
   342â†’  async storeAISuggestions(evidenceId, teamId, suggestions) {
   343â†’    try {
   344â†’      const evidenceRef = doc(db, 'teams', teamId, 'evidence', evidenceId);
   345â†’      
   346â†’      await updateDoc(evidenceRef, {
   347â†’        tagsByAI: suggestions,
   348â†’        lastAIProcessed: serverTimestamp(),
   349â†’        updatedAt: serverTimestamp()
   350â†’      });
   351â†’
   352â†’      console.log(`[AITagService] Stored ${suggestions.length} AI suggestions for ${evidenceId}`);
   353â†’    } catch (error) {
   354â†’      console.error('[AITagService] Failed to store AI suggestions:', error);
   355â†’      throw error;
   356â†’    }
   357â†’  }
   358â†’
   359â†’  /**
   360â†’   * Get MIME type from filename
   361â†’   * @param {string} filename - File name
   362â†’   * @returns {string} - MIME type
   363â†’   */
   364â†’  getMimeType(filename) {
   365â†’    if (!filename) return 'application/octet-stream';
   366â†’    
   367â†’    const ext = filename.toLowerCase().split('.').pop();
   368â†’    const mimeTypes = {
   369â†’      'pdf': 'application/pdf',
   370â†’      'jpg': 'image/jpeg',
   371â†’      'jpeg': 'image/jpeg',
   372â†’      'png': 'image/png',
   373â†’      'gif': 'image/gif',
   374â†’      'doc': 'application/msword',
   375â†’      'docx': 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
   376â†’      'xls': 'application/vnd.ms-excel',
   377â†’      'xlsx': 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
   378â†’    };
   379â†’    
   380â†’    return mimeTypes[ext] || 'application/octet-stream';
   381â†’  }
   382â†’
   383â†’  /**
   384â†’   * Approve an AI suggested tag - moves it from tagsByAI to tagsByHuman
   385â†’   * @param {string} evidenceId - Evidence document ID
   386â†’   * @param {Object} aiTag - AI tag to approve
   387â†’   * @returns {Promise<Object>} - Operation result
   388â†’   */
   389â†’  async approveAITag(evidenceId, aiTag) {
   390â†’    try {
   391â†’      const teamId = this.getTeamId();
   392â†’      const evidenceRef = doc(db, 'teams', teamId, 'evidence', evidenceId);
   393â†’      
   394â†’      // Get current evidence document
   395â†’      const evidenceSnap = await getDoc(evidenceRef);
   396â†’      if (!evidenceSnap.exists()) {
   397â†’        throw new Error(`Evidence document not found: ${evidenceId}`);
   398â†’      }
   399â†’
   400â†’      const evidenceData = evidenceSnap.data();
   401â†’      const tagsByAI = evidenceData.tagsByAI || [];
   402â†’      const tagsByHuman = evidenceData.tagsByHuman || [];
   403â†’
   404â†’      // Find the AI tag to approve
   405â†’      const aiTagIndex = tagsByAI.findIndex(tag => 
   406â†’        tag.categoryId === aiTag.categoryId && tag.tagName === aiTag.tagName
   407â†’      );
   408â†’
   409â†’      if (aiTagIndex === -1) {
   410â†’        throw new Error('AI tag not found in document');
   411â†’      }
   412â†’
   413â†’      // Create human tag from AI tag
   414â†’      const approvedTag = {
   415â†’        categoryId: aiTag.categoryId,
   416â†’        categoryName: aiTag.categoryName,
   417â†’        tagId: aiTag.tagId || `approved-tag-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`, // Generate ID if not present
   418â†’        tagName: aiTag.tagName,
   419â†’        color: aiTag.color,
   420â†’        approvedAt: serverTimestamp(),
   421â†’        originallyFromAI: true // Track that this was originally an AI suggestion
   422â†’      };
   423â†’
   424â†’      // Update the AI tag status to approved
   425â†’      const updatedAITags = [...tagsByAI];
   426â†’      updatedAITags[aiTagIndex] = {
   427â†’        ...updatedAITags[aiTagIndex],
   428â†’        status: 'approved',
   429â†’        approvedAt: serverTimestamp()
   430â†’      };
   431â†’
   432â†’      // Add to human tags
   433â†’      const updatedHumanTags = [...tagsByHuman, approvedTag];
   434â†’
   435â†’      // Update document
   436â†’      await updateDoc(evidenceRef, {
   437â†’        tagsByAI: updatedAITags,
   438â†’        tagsByHuman: updatedHumanTags,
   439â†’        updatedAt: serverTimestamp()
   440â†’      });
   441â†’
   442â†’      console.log(`[AITagService] Approved AI tag: ${aiTag.tagName} for ${evidenceId}`);
   443â†’
   444â†’      return {
   445â†’        success: true,
   446â†’        approvedTag,
   447â†’        evidenceId
   448â†’      };
   449â†’
   450â†’    } catch (error) {
   451â†’      console.error('[AITagService] Failed to approve AI tag:', error);
   452â†’      throw error;
   453â†’    }
   454â†’  }
   455â†’
   456â†’  /**
   457â†’   * Reject an AI suggested tag - updates its status to rejected
   458â†’   * @param {string} evidenceId - Evidence document ID  
   459â†’   * @param {Object} aiTag - AI tag to reject
   460â†’   * @returns {Promise<Object>} - Operation result
   461â†’   */
   462â†’  async rejectAITag(evidenceId, aiTag) {
   463â†’    try {
   464â†’      const teamId = this.getTeamId();
   465â†’      const evidenceRef = doc(db, 'teams', teamId, 'evidence', evidenceId);
   466â†’      
   467â†’      // Get current evidence document
   468â†’      const evidenceSnap = await getDoc(evidenceRef);
   469â†’      if (!evidenceSnap.exists()) {
   470â†’        throw new Error(`Evidence document not found: ${evidenceId}`);
   471â†’      }
   472â†’
   473â†’      const evidenceData = evidenceSnap.data();
   474â†’      const tagsByAI = evidenceData.tagsByAI || [];
   475â†’
   476â†’      // Find the AI tag to reject
   477â†’      const aiTagIndex = tagsByAI.findIndex(tag => 
   478â†’        tag.categoryId === aiTag.categoryId && tag.tagName === aiTag.tagName
   479â†’      );
   480â†’
   481â†’      if (aiTagIndex === -1) {
   482â†’        throw new Error('AI tag not found in document');
   483â†’      }
   484â†’
   485â†’      // Update the AI tag status to rejected
   486â†’      const updatedAITags = [...tagsByAI];
   487â†’      updatedAITags[aiTagIndex] = {
   488â†’        ...updatedAITags[aiTagIndex],
   489â†’        status: 'rejected',
   490â†’        rejectedAt: serverTimestamp()
   491â†’      };
   492â†’
   493â†’      // Update document
   494â†’      await updateDoc(evidenceRef, {
   495â†’        tagsByAI: updatedAITags,
   496â†’        updatedAt: serverTimestamp()
   497â†’      });
   498â†’
   499â†’      console.log(`[AITagService] Rejected AI tag: ${aiTag.tagName} for ${evidenceId}`);
   500â†’
   501â†’      return {
   502â†’        success: true,
   503â†’        rejectedTag: aiTag,
   504â†’        evidenceId
   505â†’      };
   506â†’
   507â†’    } catch (error) {
   508â†’      console.error('[AITagService] Failed to reject AI tag:', error);
   509â†’      throw error;
   510â†’    }
   511â†’  }
   512â†’
   513â†’  /**
   514â†’   * Process review changes from the AI review modal
   515â†’   * @param {string} evidenceId - Evidence document ID
   516â†’   * @param {Object} changes - Changes object with approved and rejected arrays
   517â†’   * @returns {Promise<Object>} - Operation result
   518â†’   */
   519â†’  async processReviewChanges(evidenceId, changes) {
   520â†’    try {
   521â†’      const { approved = [], rejected = [] } = changes;
   522â†’      const results = {
   523â†’        approvedCount: 0,
   524â†’        rejectedCount: 0,
   525â†’        errors: []
   526â†’      };
   527â†’
   528â†’      // Process approvals and rejections sequentially to avoid conflicts
   529â†’      for (const aiTag of approved) {
   530â†’        try {
   531â†’          await this.approveAITag(evidenceId, aiTag);
   532â†’          results.approvedCount++;
   533â†’        } catch (error) {
   534â†’          console.error(`Failed to approve tag ${aiTag.tagName}:`, error);
