   320‚Üí};
   321‚Üí
   322‚Üíconst handleDelete = () => {
   323‚Üí  emit('delete', props.evidence);
   324‚Üí};
   325‚Üí
   326‚Üíconst handleSelectionChange = (selected) => {
   327‚Üí  emit('selection-change', props.evidence.id, selected);
   328‚Üí};
