    73‚Üí      const docRef = await addDoc(evidenceRef, evidenceData);
    74‚Üí
    75‚Üí      console.log(`[EvidenceService] Created evidence document: ${docRef.id}`, {
    76‚Üí        metadataHash: metadataHash.substring(0, 8) + '...',
