      await updateEvidenceTags(evidenceId, updatedTags);
    } catch (err) {
      console.error('[OrganizerStore] Failed to add tag:', err);
      throw err;
    }
  };

  /**
   * Legacy method: Remove a single tag (backward compatibility)
   */
