/**
 * Search in specific tag types
 */
const searchInTagType = (evidence, searchTerm, tagType) => {
  switch (tagType) {
    case 'structured':
      const structuredTags = [...(evidence.tagsByHuman || []), ...(evidence.tagsByAI || [])];
      return structuredTags.some(tag => 
        tag.tagName?.toLowerCase().includes(searchTerm) ||
        tag.categoryName?.toLowerCase().includes(searchTerm)
      );
    case 'legacy':
      const legacyTags = evidence.legacyTags || evidence.tags || [];
      return legacyTags.some(tag => tag.toLowerCase().includes(searchTerm));
    case 'filename':
      return evidence.displayName?.toLowerCase().includes(searchTerm);
    case 'extension':
      const fileExtension = evidence.displayName?.includes('.')
        ? '.' + evidence.displayName.split('.').pop().toLowerCase()
        : '';
      return fileExtension.includes(searchTerm);
    default:
      return false;
  }
};

/**
 * Advanced search with multiple criteria
 */
const advancedSearch = (criteria) => {
  const {
    text = '',
    tagTypes = ['structured', 'legacy', 'filename'],
    categoryFilters = {},
    dateRange = null,
    fileTypes = []
  } = criteria;

  let filtered = [...coreStore.evidenceList];

  // Apply text search with specific tag types
  if (text.trim()) {
    const searchTerm = text.toLowerCase().trim();
    filtered = filtered.filter(evidence => {
      return tagTypes.some(tagType => searchInTagType(evidence, searchTerm, tagType));
    });
  }

  // Apply category filters
  Object.entries(categoryFilters).forEach(([categoryId, selectedTags]) => {
    if (selectedTags.length > 0) {
      filtered = filtered.filter(evidence => {
        const structuredTags = [...(evidence.tagsByHuman || []), ...(evidence.tagsByAI || [])];
        return structuredTags.some(tag => 
          tag.categoryId === categoryId && selectedTags.includes(tag.tagName)
        );
      });
    }
  });

  // Apply date range filter
  if (dateRange && dateRange.start && dateRange.end) {
    filtered = filtered.filter(evidence => {
      const evidenceDate = evidence.createdAt || evidence.updatedAt;
      if (!evidenceDate) return false;

      const date = evidenceDate.toDate ? evidenceDate.toDate() : new Date(evidenceDate);
      return date >= dateRange.start && date <= dateRange.end;
    });
  }

  // Apply file type filters
  if (fileTypes.length > 0) {
    filtered = filtered.filter(evidence => {
      const extension = evidence.displayName?.includes('.')
        ? evidence.displayName.split('.').pop().toLowerCase()
        : '';
      return fileTypes.includes(extension);
    });
  }

  filteredEvidence.value = filtered;
  console.log(`[OrganizerQuery] Advanced search results: ${filtered.length} documents`);

  return filtered;
};

/**
 * Get unique file extensions from evidence list
 */
const getUniqueFileExtensions = () => {
  const extensions = new Set();

  coreStore.evidenceList.forEach(evidence => {
    if (evidence.displayName?.includes('.')) {
      const extension = evidence.displayName.split('.').pop().toLowerCase();
      extensions.add(extension);
    }
  });

  return Array.from(extensions).sort();
};

/**
 * Get search suggestions based on existing tags and filenames
 */
const getSearchSuggestions = (partialText) => {
  const suggestions = new Set();
  const searchTerm = partialText.toLowerCase().trim();

  if (!searchTerm) return [];

  coreStore.evidenceList.forEach(evidence => {
    // Collect from structured tags
    const structuredTags = [...(evidence.tagsByHuman || []), ...(evidence.tagsByAI || [])];
    structuredTags.forEach(tag => {
      if (tag.tagName?.toLowerCase().includes(searchTerm)) {
        suggestions.add(tag.tagName);
      }
      if (tag.categoryName?.toLowerCase().includes(searchTerm)) {
        suggestions.add(tag.categoryName);
      }
    });

    // Collect from legacy tags
    const legacyTags = evidence.legacyTags || evidence.tags || [];
    legacyTags.forEach(tag => {
      if (tag.toLowerCase().includes(searchTerm)) {
        suggestions.add(tag);
      }
    });

    // Collect from filenames (partial matches)
    if (evidence.displayName?.toLowerCase().includes(searchTerm)) {
      suggestions.add(evidence.displayName);
    }
  });

  return Array.from(suggestions).slice(0, 10); // Limit to 10 suggestions
};

