```javascript
rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {
    // Files accessible only by team members
    match /files/{hash} {
      allow read, write: if request.auth != null 
        && resource.data.teamId == request.auth.token.teamId;
    }

    // Team file index
