}

// File versions - when user chooses to keep duplicates (team-scoped)
/teams/{teamId}/files/{hash}/versions/{versionId}
{
  originalHash: string,
  versionId: string,
  fileName: string,
  uploadedAt: Timestamp,
  uploadedBy: string,
  reason: 'user_choice' | 'metadata_diff'
}
