   111â†’<script setup>
   112â†’import { ref, computed } from 'vue';
   113â†’import FileUploadQueue from '../components/features/upload/FileUploadQueue.vue';
   114â†’import UploadDropzone from '../components/features/upload/UploadDropzone.vue';
   115â†’import FolderOptionsDialog from '../components/features/upload/FolderOptionsDialog.vue';
   116â†’import ProcessingProgressModal from '../components/features/upload/ProcessingProgressModal.vue';
   117â†’import CloudFileWarningModal from '../components/features/upload/CloudFileWarningModal.vue';
   118â†’import { useFileQueue } from '../composables/useFileQueue.js';
   119â†’import { useFileDragDrop } from '../composables/useFileDragDrop.js';
   120â†’import { useQueueDeduplication } from '../composables/useQueueDeduplication.js';
   121â†’import { useFolderOptions } from '../composables/useFolderOptions.js';
   122â†’import { useTimeBasedWarning } from '../composables/useTimeBasedWarning.js';
   123â†’import {
   124â†’  calculateCalibratedProcessingTime,
   125â†’  getStoredHardwarePerformanceFactor,
   126â†’} from '../utils/hardwareCalibration.js';
   127â†’import { storage } from '../services/firebase.js';
   128â†’import { ref as storageRef, getDownloadURL, uploadBytesResumable } from 'firebase/storage';
   129â†’import { useAuthStore } from '../stores/auth.js';
   130â†’import { useLazyHashTooltip } from '../composables/useLazyHashTooltip.js';
   131â†’
   132â†’// Component configuration
   133â†’defineOptions({
   134â†’  name: 'FileUploadView',
   135â†’});
   136â†’
   137â†’// Template refs
   138â†’const dropzoneRef = ref(null);
   139â†’
   140â†’// Auth store
   141â†’const authStore = useAuthStore();
   142â†’
   143â†’// Hash tooltip system for cache management
   144â†’const { populateExistingHash } = useLazyHashTooltip();
   145â†’
   146â†’// Composables
   147â†’const {
   148â†’  uploadQueue,
   149â†’  showSingleFileNotification,
   150â†’  singleFileNotification,
   151â†’  fileInput,
   152â†’  folderInput,
   153â†’  processingProgress,
   154â†’  isProcessingUIUpdate,
   155â†’  uiUpdateProgress,
   156â†’  uploadStatus,
   157â†’  triggerFileSelect,
   158â†’  triggerFolderSelect,
   159â†’  processSingleFile,
   160â†’  addFilesToQueue,
   161â†’  initializeQueueInstantly,
   162â†’  updateUploadQueue,
   163â†’  removeFromQueue,
   164â†’  showNotification,
   165â†’  setPhaseComplete,
   166â†’  resetUploadStatus,
   167â†’  updateUploadStatus,
   168â†’} = useFileQueue();
   169â†’
   170â†’const {
   171â†’  isDragOver,
   172â†’  handleDragOver,
   173â†’  handleDragLeave,
   174â†’  handleDrop: baseHandleDrop,
   175â†’} = useFileDragDrop();
   176â†’
   177â†’const queueDeduplication = useQueueDeduplication();
   178â†’const { processFiles } = queueDeduplication;
   179â†’
   180â†’const {
   181â†’  showFolderOptions,
   182â†’  includeSubfolders,
   183â†’  subfolderCount,
   184â†’  isAnalyzing,
   185â†’  mainFolderAnalysis,
   186â†’  allFilesAnalysis,
   187â†’  totalDirectoryEntryCount,
   188â†’  // Timeout state
   189â†’  analysisTimedOut,
   190â†’  timeoutError,
   191â†’  currentProgressMessage,
   192â†’  skippedFolders,
   193â†’  // Progress tracking
   194â†’  mainFolderProgress,
   195â†’  allFilesProgress,
   196â†’  mainFolderComplete,
   197â†’  allFilesComplete,
   198â†’  isAnalyzingMainFolder,
   199â†’  isAnalyzingAllFiles,
   200â†’  processFolderEntry,
   201â†’  processFolderFiles,
   202â†’  confirmFolderOptions: baseConfirmFolderOptions,
   203â†’  cancelFolderUpload,
   204â†’} = useFolderOptions();
   205â†’
   206â†’const timeWarning = useTimeBasedWarning();
   207â†’
   208â†’// Upload state tracking  
   209â†’const isStartingUpload = ref(false);
   210â†’const isPaused = ref(false);
   211â†’const pauseRequested = ref(false);
   212â†’const currentUploadIndex = ref(0);
   213â†’let uploadAbortController = null;
   214â†’
   215â†’// Simple hash calculation function (same as worker)
   216â†’const calculateFileHash = async (file) => {
   217â†’  try {
   218â†’    const buffer = await file.arrayBuffer();
   219â†’    const hashBuffer = await crypto.subtle.digest('SHA-256', buffer);
   220â†’    const hashArray = Array.from(new Uint8Array(hashBuffer));
   221â†’    const hash = hashArray.map(b => b.toString(16).padStart(2, '0')).join('');
   222â†’    
   223â†’    // Add size suffix for collision safety (SHA-256 + size = virtually impossible collision)
   224â†’    return `${hash}_${file.size}`;
   225â†’  } catch (error) {
   226â†’    throw new Error(`Failed to generate hash for file ${file.name}: ${error.message}`);
   227â†’  }
   228â†’};
   229â†’
   230â†’// Simple upload helper functions
   231â†’const generateStoragePath = (fileHash, originalFileName) => {
   232â†’  const extension = originalFileName.split('.').pop();
   233â†’  return `teams/${authStore.currentTeam}/files/${fileHash}.${extension}`;
   234â†’};
   235â†’
   236â†’const checkFileExists = async (fileHash, originalFileName) => {
   237â†’  try {
   238â†’    const storagePath = generateStoragePath(fileHash, originalFileName);
   239â†’    const storageReference = storageRef(storage, storagePath);
   240â†’    await getDownloadURL(storageReference);
   241â†’    return true;
   242â†’  } catch {
   243â†’    return false;
   244â†’  }
   245â†’};
   246â†’
   247â†’const uploadSingleFile = async (file, fileHash, originalFileName, abortSignal = null) => {
   248â†’    const storagePath = generateStoragePath(fileHash, originalFileName);
   249â†’    const storageReference = storageRef(storage, storagePath);
   250â†’    
   251â†’    const uploadTask = uploadBytesResumable(storageReference, file, {
   252â†’      customMetadata: {
   253â†’        teamId: authStore.currentTeam,
   254â†’        userId: authStore.user.uid,
   255â†’        originalName: originalFileName,
   256â†’        hash: fileHash,
   257â†’      },
   258â†’    });
   259â†’
   260â†’    return new Promise((resolve, reject) => {
   261â†’      // Handle abort signal
   262â†’      if (abortSignal) {
   263â†’        abortSignal.addEventListener('abort', () => {
   264â†’          uploadTask.cancel();
   265â†’          reject(new Error('AbortError'));
   266â†’        });
   267â†’      }
   268â†’
   269â†’      uploadTask.on(
   270â†’        'state_changed',
   271â†’        () => {
   272â†’          // Check if aborted during progress
   273â†’          if (abortSignal && abortSignal.aborted) {
   274â†’            uploadTask.cancel();
   275â†’            reject(new Error('AbortError'));
   276â†’            return;
   277â†’          }
   278â†’          // Progress updates could be added here if needed
   279â†’        },
   280â†’        (error) => {
   281â†’          reject(error);
   282â†’        },
   283â†’        async () => {
   284â†’          // Upload completed successfully
   285â†’          try {
   286â†’            const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);
   287â†’            resolve({ success: true, downloadURL });
   288â†’          } catch (error) {
   289â†’            reject(error);
   290â†’          }
   291â†’        }
   292â†’      );
   293â†’    });
   294â†’};
   295â†’
   296â†’// Connect time monitoring to deduplication processing
   297â†’queueDeduplication.setTimeMonitoringCallback({
   298â†’  onProcessingStart: () => {
   299â†’    // Time monitoring will be started when we have an estimate from folder analysis
   300â†’  },
   301â†’  onProcessingComplete: () => {
   302â†’    timeWarning.resetMonitoring();
   303â†’    queueDeduplication.terminateWorker();
   304â†’  },
   305â†’  onProcessingError: (error) => {
   306â†’    timeWarning.resetMonitoring();
   307â†’    queueDeduplication.terminateWorker();
   308â†’    console.log('File processing error - time monitoring reset and workers terminated:', error);
   309â†’  },
   310â†’  onProcessingAborted: () => {
   311â†’    queueDeduplication.terminateWorker();
   312â†’    console.log('File processing aborted - workers terminated');
   313â†’  },
   314â†’});
   315â†’
   316â†’// Legacy: Complex cleanup function replaced by simple page refresh
   317â†’
   318â†’// Enhanced clearQueue that uses comprehensive cleanup
   319â†’const clearQueue = () => {
   320â†’  // Simple and reliable: just refresh the page
   321â†’  console.log('ðŸ”„ Clear All: Refreshing page to reset all state');
   322â†’  window.location.reload();
   323â†’};
   324â†’
   325â†’
   326â†’// Integrate processFiles with updateUploadQueue with safety filtering
   327â†’const processFilesWithQueue = async (files) => {
   328â†’  const processId = Math.random().toString(36).substr(2, 9);
   329â†’
   330â†’  // Check if analysis has been aborted before processing
   331â†’  if (analysisTimedOut.value) {
   332â†’    console.log('Skipping file processing - analysis was aborted');
   333â†’    return;
   334â†’  }
   335â†’
   336â†’  // Additional safety check - if queue is empty, processing was likely cancelled
   337â†’  if (!files || files.length === 0) {
   338â†’    console.log('Skipping file processing - no files to process');
   339â†’    return;
   340â†’  }
   341â†’
   342â†’  // Start time monitoring if not already started and we have files to process
   343â†’  if (files.length > 0 && !timeWarning.startTime.value) {
   344â†’    const folderAnalysis = allFilesAnalysis.value || mainFolderAnalysis.value;
   345â†’    let estimatedTime = 0;
   346â†’
   347â†’    if (folderAnalysis && folderAnalysis.timeMs && folderAnalysis.timeMs > 0) {
   348â†’      // Use existing folder analysis estimate (already hardware-calibrated)
   349â†’      estimatedTime = folderAnalysis.timeMs;
   350â†’    } else {
   351â†’      // Generate hardware-calibrated estimate on-the-fly for files without folder analysis
   352â†’      // No folder analysis available, generating hardware-calibrated estimate for file processing
   353â†’
   354â†’      // Get stored hardware performance factor or use baseline
   355â†’      let hardwarePerformanceFactor = getStoredHardwarePerformanceFactor();
   356â†’      if (!hardwarePerformanceFactor || hardwarePerformanceFactor <= 0) {
   357â†’        // Use baseline H-factor for new users (1.61 files/ms)
   358â†’        hardwarePerformanceFactor = 1.61;
   359â†’      }
   360â†’
   361â†’      // Create file analysis data for calibrated estimation
   362â†’      const sizeMap = new Map();
   363â†’      files.forEach((file) => {
   364â†’        const size = file.size || 0;
   365â†’        sizeMap.set(size, (sizeMap.get(size) || 0) + 1);
   366â†’      });
   367â†’      const duplicateCandidates = files.filter((file) => sizeMap.get(file.size || 0) > 1);
   368â†’      const duplicateCandidatesSizeMB =
   369â†’        duplicateCandidates.reduce((sum, file) => sum + (file.size || 0), 0) / (1024 * 1024);
   370â†’
   371â†’      const folderData = {
   372â†’        totalFiles: files.length,
   373â†’        duplicateCandidates: duplicateCandidates.length,
   374â†’        duplicateCandidatesSizeMB: Math.round(duplicateCandidatesSizeMB * 10) / 10,
   375â†’        avgDirectoryDepth: 2.5, // Default assumption for direct file processing
   376â†’        totalDirectoryCount: 1,
   377â†’      };
   378â†’
   379â†’      // Generate hardware-calibrated estimate
   380â†’      const calibratedPrediction = calculateCalibratedProcessingTime(
   381â†’        folderData,
   382â†’        hardwarePerformanceFactor,
   383â†’      );
   384â†’      estimatedTime = calibratedPrediction.totalTimeMs;
   385â†’
   386â†’    }
   387â†’
   388â†’    if (estimatedTime > 0) {
   389â†’      timeWarning.startMonitoring(estimatedTime);
   390â†’    } else {
   391â†’      console.warn('Time monitoring not started: Unable to generate hardware-calibrated estimate');
   392â†’    }
   393â†’  }
   394â†’
   395â†’  // Check if processing was aborted after time monitoring setup
   396â†’  if (analysisTimedOut.value) {
   397â†’    console.log('Aborting file processing - analysis was cancelled after time monitoring setup');
   398â†’    return;
   399â†’  }
   400â†’
   401â†’  // Double-check safety filter: exclude any files from skipped folders
   402â†’  if (skippedFolders.value && skippedFolders.value.length > 0) {
   403â†’    const originalCount = files.length;
   404â†’    const safeFiles = files.filter((file) => {
   405â†’      const filePath = file.path || file.webkitRelativePath || file.name;
   406â†’      const isInSkippedFolder = skippedFolders.value.some((skippedPath) => {
   407â†’        const normalizedFilePath = filePath.replace(/\\/g, '/').toLowerCase();
   408â†’        const normalizedSkippedPath = skippedPath.replace(/\\/g, '/').toLowerCase();
   409â†’        return normalizedFilePath.startsWith(normalizedSkippedPath);
   410â†’      });
   411â†’
   412â†’      if (isInSkippedFolder) {
   413â†’        console.log(`Upload safety filter: excluding ${filePath} from skipped folder`);
   414â†’        return false;
   415â†’      }
   416â†’      return true;
   417â†’    });
   418â†’
   419â†’    if (safeFiles.length !== originalCount) {
   420â†’      console.log(
   421â†’        `Upload safety filter: excluded ${originalCount - safeFiles.length} files from ${skippedFolders.value.length} skipped folders`,
   422â†’      );
   423â†’    }
   424â†’
   425â†’    // Final check before actually processing files
   426â†’    if (analysisTimedOut.value) {
   427â†’      console.log(`âŒ [${processId}] EXIT EARLY: aborted during file preparation`);
   428â†’      return;
   429â†’    }
   430â†’
   431â†’    try {
   432â†’      // Final abort check immediately before processFiles call
   433â†’      if (analysisTimedOut.value) {
   434â†’        console.log(`âŒ [${processId}] EXIT EARLY: aborted right before processFiles call`);
   435â†’        return;
   436â†’      }
   437â†’
   438â†’      await processFiles(safeFiles, updateUploadQueue);
   439â†’
   440â†’      // Check if processing was aborted after processFiles completed
   441â†’      if (analysisTimedOut.value) {
   442â†’        console.log(
   443â†’          `âŒ [${processId}] EXIT ABORT: processing aborted after completion - skipping cleanup`,
   444â†’        );
   445â†’        return;
   446â†’      }
   447â†’
   448â†’      // Processing completed successfully - perform cleanup
   449â†’      timeWarning.resetMonitoring();
   450â†’      queueDeduplication.clearTimeMonitoringCallback();
   451â†’    } catch (error) {
   452â†’      console.error('Error during file processing:', error);
   453â†’
   454â†’      // Check if error was due to abort
   455â†’      if (analysisTimedOut.value) {
   456â†’        console.log(`âŒ [${processId}] EXIT ERROR ABORT: processing aborted during error handling`);
   457â†’        return;
   458â†’      }
   459â†’
   460â†’      // Cleanup on error as well
   461â†’      timeWarning.resetMonitoring();
   462â†’      queueDeduplication.clearTimeMonitoringCallback();
   463â†’      throw error; // Re-throw to maintain error handling
   464â†’    }
   465â†’  } else {
   466â†’    // Final check before actually processing files (no folder filtering path)
   467â†’    if (analysisTimedOut.value) {
   468â†’      console.log('Aborting file processing - analysis was cancelled during preparation');
   469â†’      return;
   470â†’    }
   471â†’
   472â†’    try {
   473â†’      // Final abort check immediately before processFiles call
   474â†’      if (analysisTimedOut.value) {
   475â†’        console.log(`âŒ [${processId}] EXIT EARLY: aborted right before processFiles call`);
   476â†’        return;
   477â†’      }
   478â†’
   479â†’      await processFiles(files, updateUploadQueue);
   480â†’
   481â†’      // Check if processing was aborted after processFiles completed
   482â†’      if (analysisTimedOut.value) {
   483â†’        console.log(
   484â†’          `âŒ [${processId}] EXIT ABORT: processing aborted after completion - skipping cleanup`,
   485â†’        );
   486â†’        return;
   487â†’      }
   488â†’
   489â†’      // Processing completed successfully - perform cleanup
   490â†’      timeWarning.resetMonitoring();
   491â†’      queueDeduplication.clearTimeMonitoringCallback();
   492â†’    } catch (error) {
   493â†’      console.error('Error during file processing:', error);
   494â†’
   495â†’      // Check if error was due to abort
   496â†’      if (analysisTimedOut.value) {
   497â†’        console.log(`âŒ [${processId}] EXIT ERROR ABORT: processing aborted during error handling`);
   498â†’        return;
   499â†’      }
   500â†’
   501â†’      // Cleanup on error as well
   502â†’      timeWarning.resetMonitoring();
   503â†’      queueDeduplication.clearTimeMonitoringCallback();
   504â†’      throw error; // Re-throw to maintain error handling
   505â†’    }
   506â†’  }
   507â†’
   508â†’  // ðŸ” DEBUG: Log AsyncTracker state at successful exit
   509â†’  if (window.__asyncTracker) {
   510â†’    const exitStats = window.__asyncTracker.stats();
   511â†’    console.log(`ðŸ“Š [${processId}] AsyncTracker at EXIT:`, exitStats);
   512â†’  }
   513â†’};
   514â†’
   515â†’// Event handlers with composable integration
   516â†’const handleFileSelect = async (event) => {
   517â†’  updateRefs();
   518â†’  const files = Array.from(event.target.files);
   519â†’  if (files.length === 1) {
   520â†’    await processSingleFile(files[0], processFilesWithQueue);
   521â†’    showNotification('File ready for upload', 'success');
   522â†’  } else {
   523â†’    await addFilesToQueue(files, processFilesWithQueue);
   524â†’  }
   525â†’  // Reset input
   526â†’  event.target.value = '';
   527â†’};
   528â†’
   529â†’const handleFolderSelect = (event) => {
   530â†’  updateRefs();
   531â†’  const files = Array.from(event.target.files);
   532â†’
   533â†’  // File Explorer count will be compared automatically in processing
   534â†’  // Pass callback to handle no-subfolder case automatically
   535â†’  processFolderFiles(files, async (files) => {
