   616â†’};
   617â†’
   618â†’const handleCloseWarning = () => {
   619â†’  // User closed warning without choosing action
   620â†’  timeWarning.dismissWarning();
   621â†’};
   622â†’
   623â†’// Pause/Resume upload handlers
   624â†’const handlePauseUpload = () => {
   625â†’  console.log('Pause upload requested');
   626â†’  pauseRequested.value = true;
   627â†’  updateUploadStatus('requestPause');
   628â†’};
   629â†’
   630â†’const handleResumeUpload = () => {
   631â†’  console.log('Resume upload requested');
   632â†’  isPaused.value = false;
   633â†’  updateUploadStatus('resume');
   634â†’  
   635â†’  // Continue upload from where we left off
   636â†’  continueUpload();
   637â†’};
