   639â†’// Resumable upload loop function
   640â†’const continueUpload = async () => {
   641â†’  try {
   642â†’    console.log('Continuing upload process...');
   643â†’
   644â†’    // Get files that are ready for upload (not duplicates)
   645â†’    const filesToUpload = uploadQueue.value.filter(file => !file.isDuplicate);
   646â†’    
   647â†’    if (filesToUpload.length === 0) {
   648â†’      showNotification('No files to upload (all are duplicates)', 'info');
   649â†’      updateUploadStatus('complete');
   650â†’      return;
   651â†’    }
   652â†’
   653â†’    // Start from current index or beginning
   654â†’    const startIndex = uploadStatus.value.currentUploadIndex || 0;
   655â†’    console.log(`Continuing from file ${startIndex + 1} of ${filesToUpload.length}`);
   656â†’
   657â†’    // Process files from current index
   658â†’    for (let i = startIndex; i < filesToUpload.length; i++) {
   659â†’      // Check if pause was requested before processing next file
   660â†’      if (pauseRequested.value) {
   661â†’        console.log(`Pause requested at file ${i + 1}. Pausing...`);
   662â†’        updateUploadStatus('setUploadIndex', i); // Save current position
   663â†’        updateUploadStatus('pause');
   664â†’        isPaused.value = true;
   665â†’        pauseRequested.value = false;
   666â†’        return;
