   671â†’      
   672â†’      try {
   673â†’        // Create abort controller for this upload
   674â†’        uploadAbortController = new AbortController();
   675â†’
   676â†’        // Calculate hash for the file (on-demand during upload)
   677â†’        updateUploadStatus('currentFile', queueFile.name, 'calculating_hash');
   678â†’        console.log(`Calculating hash for: ${queueFile.name}`);
   679â†’        let fileHash;
   680â†’        
   681â†’        // Check if file already has a hash (from deduplication process)
   682â†’        if (queueFile.hash) {
   683â†’          fileHash = queueFile.hash;
   684â†’          console.log(`Using existing hash for: ${queueFile.name}`);
   685â†’        } else {
   686â†’          // Calculate hash now (for files with unique sizes or not previously processed)
   687â†’          fileHash = await calculateFileHash(queueFile.file);
   688â†’          // Store calculated hash in the queue file for reuse by tooltip system
   689â†’          queueFile.hash = fileHash;
   690â†’          // Also populate the tooltip cache immediately for instant tooltips
   691â†’          populateExistingHash(queueFile.id || queueFile.name, fileHash);
   692â†’          console.log(`Calculated new hash for: ${queueFile.name}`);
   693â†’        }
   694â†’
   695â†’        // Check if upload was aborted during hash calculation
   696â†’        if (uploadAbortController.signal.aborted) {
   697â†’          console.log(`Upload aborted during hash calculation for: ${queueFile.name}`);
   698â†’          break;
   699â†’        }
   700â†’
   701â†’        // Check if file already exists in Firebase Storage
   702â†’        updateUploadStatus('currentFile', queueFile.name, 'checking_existing');
   703â†’        const fileExists = await checkFileExists(fileHash, queueFile.name);
   704â†’        
   705â†’        if (fileExists) {
   706â†’          // File already uploaded previously
   707â†’          console.log(`File already exists: ${queueFile.name}`);
   708â†’          updateUploadStatus('previouslyUploaded');
   709â†’        } else {
   710â†’          // Check if upload was aborted before uploading
   711â†’          if (uploadAbortController.signal.aborted) {
   712â†’            console.log(`Upload aborted before uploading: ${queueFile.name}`);
   713â†’            break;
   714â†’          }
   715â†’          
   716â†’          // File needs to be uploaded
   717â†’          updateUploadStatus('currentFile', queueFile.name, 'uploading');
   718â†’          console.log(`Uploading file: ${queueFile.name}`);
   719â†’          await uploadSingleFile(queueFile.file, fileHash, queueFile.name, uploadAbortController.signal);
   720â†’          updateUploadStatus('successful');
   721â†’          console.log(`Successfully uploaded: ${queueFile.name}`);
   722â†’        }
   723â†’      } catch (error) {
   724â†’        if (error.name === 'AbortError') {
   725â†’          console.log(`Upload aborted for: ${queueFile.name}`);
   726â†’          break;
   727â†’        }
   728â†’        console.error(`Failed to upload ${queueFile.name}:`, error);
   729â†’        updateUploadStatus('failed');
   730â†’      } finally {
   731â†’        uploadAbortController = null;
   732â†’      }
   733â†’    }
   734â†’
   735â†’    // Check if we completed all files or were paused
   736â†’    if (!isPaused.value) {
   737â†’      updateUploadStatus('complete');
   738â†’      console.log('Upload process completed:', {
   739â†’        successful: uploadStatus.value.successful,
   740â†’        failed: uploadStatus.value.failed,
   741â†’        previouslyUploaded: uploadStatus.value.previouslyUploaded
   742â†’      });
   743â†’
   744â†’      // Show completion notification
   745â†’      const totalProcessed = uploadStatus.value.successful + uploadStatus.value.previouslyUploaded;
   746â†’      if (uploadStatus.value.failed === 0) {
   747â†’        showNotification(`All ${totalProcessed} files processed successfully!`, 'success');
   748â†’      } else {
   749â†’        showNotification(`${totalProcessed} files processed, ${uploadStatus.value.failed} failed`, 'warning');
   750â†’      }
   751â†’      
   752â†’      currentUploadIndex.value = 0;
   753â†’      isStartingUpload.value = false;
   754â†’    }
   755â†’
   756â†’  } catch (error) {
   757â†’    console.error('Upload process failed:', error);
   758â†’    showNotification('Upload failed: ' + (error.message || 'Unknown error'), 'error');
   759â†’    updateUploadStatus('complete');
   760â†’    isPaused.value = false;
   761â†’    currentUploadIndex.value = 0;
   762â†’    isStartingUpload.value = false;
   763â†’  }
   764â†’};
   765â†’
   766â†’// Simple upload control handler
   767â†’const handleStartUpload = async () => {
   768â†’  try {
   769â†’    isStartingUpload.value = true;
   770â†’    setPhaseComplete();
   771â†’    
   772â†’    // Reset upload status counters only if not resuming
   773â†’    if (!isPaused.value) {
   774â†’      resetUploadStatus();
   775â†’      currentUploadIndex.value = 0;
   776â†’    }
   777â†’    
   778â†’    updateUploadStatus('start');
   779â†’    console.log('Starting upload process...');
   780â†’    
   781â†’    // Start the upload loop
   782â†’    await continueUpload();
   783â†’
   784â†’  } catch (error) {
   785â†’    console.error('Upload process failed:', error);
   786â†’    showNotification('Upload failed: ' + (error.message || 'Unknown error'), 'error');
   787â†’    updateUploadStatus('complete');
   788â†’    isPaused.value = false;
   789â†’    currentUploadIndex.value = 0;
   790â†’    isStartingUpload.value = false;
   791â†’  }
   792â†’};
   793â†’
   794â†’// Legacy upload handlers removed - using simple upload system now
   795â†’
   796â†’// Computed property for total analyzed files count
   797â†’// Use directory entry count (File Explorer count) instead of processed files count
   798â†’const totalAnalyzedFiles = computed(() => {
   799â†’  // Return the directory entry count if available (shows what File Explorer sees)
   800â†’  return totalDirectoryEntryCount.value > 0 ? totalDirectoryEntryCount.value : null;
   801â†’});
   802â†’</script>
