     1→import { defineStore } from 'pinia';
     2→import { ref, computed } from 'vue';
     3→import { collection, query, where, orderBy, onSnapshot, doc, addDoc, updateDoc, deleteDoc, serverTimestamp } from 'firebase/firestore';
     4→import { db } from '../../../services/firebase.js';
     5→import { useAuthStore } from '../../../core/stores/auth.js';
     6→
     7→export const useCategoryStore = defineStore('category', () => {
     8→  // State
     9→  const categories = ref([]);
    10→  const loading = ref(false);
    11→  const error = ref(null);
    12→  const isInitialized = ref(false);
    13→  
    14→  // Auth store reference
    15→  const authStore = useAuthStore();
    16→
    17→  // Computed
    18→  const categoryCount = computed(() => categories.value.length);
    19→  const activeCategories = computed(() => categories.value.filter(cat => cat.isActive));
    20→  
    21→  // Default category colors palette
    22→  const defaultColors = [
    23→    '#1976d2', // Blue - Document Type
    24→    '#388e3c', // Green - Date/Period  
    25→    '#f57c00', // Orange - Institution
    26→    '#7b1fa2', // Purple
    27→    '#d32f2f', // Red
    28→    '#455a64', // Blue Grey
    29→    '#00796b', // Teal
    30→    '#f57f17', // Yellow
    31→    '#e91e63', // Pink
    32→    '#795548', // Brown
    33→  ];
    34→
    35→  /**
    36→   * Load categories from Firestore with real-time updates
    37→   */
    38→  const loadCategories = async () => {
    39→    if (!authStore.isAuthenticated) {
    40→      error.value = 'User not authenticated';
    41→      return;
    42→    }
    43→
    44→    try {
    45→      loading.value = true;
    46→      error.value = null;
    47→
    48→      const teamId = authStore.currentTeam;
    49→      if (!teamId) {
    50→        throw new Error('No team ID available');
    51→      }
    52→
    53→      // Create query for categories collection
    54→      const categoriesRef = collection(db, 'teams', teamId, 'categories');
    55→      const categoriesQuery = query(
    56→        categoriesRef,
    57→        where('isActive', '==', true),
    58→        orderBy('createdAt', 'asc')
    59→      );
    60→
    61→      // Set up real-time listener
    62→      const unsubscribe = onSnapshot(
    63→        categoriesQuery,
    64→        (snapshot) => {
    65→          const loadedCategories = [];
    66→          
    67→          snapshot.docs.forEach(doc => {
    68→            loadedCategories.push({
    69→              id: doc.id,
    70→              ...doc.data()
    71→            });
    72→          });
    73→
    74→          categories.value = loadedCategories;
    75→          loading.value = false;
    76→          isInitialized.value = true;
    77→
    78→          console.log(`[CategoryStore] Loaded ${loadedCategories.length} categories`);
    79→        },
    80→        (err) => {
    81→          console.error('[CategoryStore] Error loading categories:', err);
    82→          error.value = err.message;
    83→          loading.value = false;
    84→        }
    85→      );
    86→
    87→      return unsubscribe;
    88→    } catch (err) {
    89→      console.error('[CategoryStore] Failed to load categories:', err);
    90→      error.value = err.message;
    91→      loading.value = false;
    92→    }
    93→  };
    94→
    95→  /**
    96→   * Create a new category
    97→   */
    98→  const createCategory = async (categoryData) => {
    99→    try {
   100→      if (!authStore.isAuthenticated) {
   101→        throw new Error('User not authenticated');
   102→      }
   103→
   104→      const teamId = authStore.currentTeam;
   105→      if (!teamId) {
   106→        throw new Error('No team ID available');
   107→      }
   108→
   109→      // Validate required fields
   110→      if (!categoryData.name || !categoryData.name.trim()) {
   111→        throw new Error('Category name is required');
   112→      }
   113→
   114→      // Check for duplicate names
   115→      const existingCategory = categories.value.find(
   116→        cat => cat.name.toLowerCase() === categoryData.name.trim().toLowerCase()
   117→      );
   118→      if (existingCategory) {
   119→        throw new Error('Category name already exists');
   120→      }
   121→
   122→      // Assign default color if not provided
   123→      const color = categoryData.color || getNextDefaultColor();
   124→
   125→      const newCategory = {
   126→        name: categoryData.name.trim(),
   127→        color,
   128→        tags: categoryData.tags || [],
   129→        isActive: true,
   130→        createdAt: serverTimestamp(),
   131→        updatedAt: serverTimestamp()
   132→      };
   133→
   134→      const categoriesRef = collection(db, 'teams', teamId, 'categories');
   135→      const docRef = await addDoc(categoriesRef, newCategory);
   136→
   137→      console.log(`[CategoryStore] Created category: ${categoryData.name}`);
   138→      return docRef.id;
   139→    } catch (err) {
   140→      console.error('[CategoryStore] Failed to create category:', err);
   141→      throw err;
   142→    }
   143→  };
   144→
   145→  /**
   146→   * Update an existing category
   147→   */
   148→  const updateCategory = async (categoryId, updates) => {
   149→    try {
   150→      if (!authStore.isAuthenticated) {
   151→        throw new Error('User not authenticated');
   152→      }
   153→
   154→      const teamId = authStore.currentTeam;
   155→      if (!teamId) {
   156→        throw new Error('No team ID available');
   157→      }
   158→
   159→      // Validate name if being updated
   160→      if (updates.name) {
   161→        const existingCategory = categories.value.find(
   162→          cat => cat.id !== categoryId && 
   163→          cat.name.toLowerCase() === updates.name.trim().toLowerCase()
   164→        );
   165→        if (existingCategory) {
   166→          throw new Error('Category name already exists');
   167→        }
   168→      }
   169→
   170→      const categoryRef = doc(db, 'teams', teamId, 'categories', categoryId);
   171→      await updateDoc(categoryRef, {
   172→        ...updates,
   173→        updatedAt: serverTimestamp()
   174→      });
   175→
   176→      console.log(`[CategoryStore] Updated category: ${categoryId}`);
   177→      return true;
   178→    } catch (err) {
   179→      console.error('[CategoryStore] Failed to update category:', err);
   180→      throw err;
   181→    }
   182→  };
   183→
   184→  /**
   185→   * Delete a category (soft delete)
   186→   */
   187→  const deleteCategory = async (categoryId) => {
   188→    try {
   189→      if (!authStore.isAuthenticated) {
   190→        throw new Error('User not authenticated');
   191→      }
   192→
   193→      const teamId = authStore.currentTeam;
   194→      if (!teamId) {
   195→        throw new Error('No team ID available');
   196→      }
   197→
   198→      const categoryRef = doc(db, 'teams', teamId, 'categories', categoryId);
   199→      await updateDoc(categoryRef, {
   200→        isActive: false,
   201→        updatedAt: serverTimestamp()
   202→      });
   203→
   204→      console.log(`[CategoryStore] Deleted category: ${categoryId}`);
   205→      return true;
   206→    } catch (err) {
   207→      console.error('[CategoryStore] Failed to delete category:', err);
   208→      throw err;
   209→    }
   210→  };
   211→
   212→  /**
   213→   * Get next available default color
   214→   */
   215→  const getNextDefaultColor = () => {
   216→    const usedColors = categories.value.map(cat => cat.color);
   217→    const availableColor = defaultColors.find(color => !usedColors.includes(color));
   218→    return availableColor || defaultColors[0]; // Fallback to first color if all used
   219→  };
   220→
   221→  /**
   222→   * Generate color variation for tags within a category
   223→   */
   224→  const generateTagColor = (baseColor, index = 0) => {
   225→    // Simple color variation by adjusting lightness
   226→    const variations = [
   227→      baseColor, // Original color
   228→      adjustColor(baseColor, -20), // Darker
   229→      adjustColor(baseColor, 20),  // Lighter
   230→      adjustColor(baseColor, -40), // Much darker
   231→      adjustColor(baseColor, 40),  // Much lighter
