   795â†’              originalName: queueFile.name,
   796â†’              lastModified: queueFile.lastModified,
   797â†’              fileHash: fileHash,
   798â†’              sessionId: getCurrentSessionId(),
   799â†’              originalPath: queueFile.path, // webkitRelativePath for folder uploads
   800â†’            });
   801â†’          } catch (metadataError) {
   802â†’            console.error(
   803â†’              `Failed to create metadata record for existing file ${queueFile.name}:`,
   804â†’              metadataError
   805â†’            );
   806â†’            // Don't fail the upload process for metadata errors
   807â†’          }
   808â†’        } else {
   809â†’          // Check if upload was aborted before uploading
   810â†’          if (uploadAbortController.signal.aborted) {
   811â†’            console.log(`Upload aborted before uploading: ${queueFile.name}`);
   812â†’            break;
   813â†’          }
   814â†’
   815â†’          // File needs to be uploaded
   816â†’          updateUploadStatus('currentFile', queueFile.name, 'uploading');
   817â†’          console.log(`Uploading file: ${queueFile.name}`);
   818â†’
   819â†’          // Log upload start event
   820â†’          try {
   821â†’            const metadataHash = await generateMetadataHash(
   822â†’              queueFile.name,
   823â†’              queueFile.lastModified,
   824â†’              fileHash
   825â†’            );
   826â†’            await logUploadEvent({
   827â†’              eventType: 'upload_interrupted',
   828â†’              fileName: queueFile.name,
   829â†’              fileHash: fileHash,
   830â†’              metadataHash: metadataHash,
   831â†’            });
   832â†’          } catch (logError) {
   833â†’            console.error(
   834â†’              `Failed to log interrupted upload event for ${queueFile.name}:`,
   835â†’              logError
   836â†’            );
   837â†’            // Don't fail the upload process for logging errors
   838â†’          }
   839â†’
   840â†’          const uploadStartTime = Date.now();
