   849â†’          updateUploadStatus('successful');
   850â†’          updateFileStatus(queueFile, 'completed');
   851â†’          console.log(`Successfully uploaded: ${queueFile.name}`);
   852â†’
   853â†’          // Log successful upload completion
   854â†’          try {
   855â†’            const metadataHash = await generateMetadataHash(
   856â†’              queueFile.name,
   857â†’              queueFile.lastModified,
   858â†’              fileHash
   859â†’            );
   860â†’            await logUploadEvent({
