   862â†’              fileName: queueFile.name,
   863â†’              fileHash: fileHash,
   864â†’              metadataHash: metadataHash,
   865â†’            });
   866â†’          } catch (logError) {
   867â†’            console.error(`Failed to log upload completion event for ${queueFile.name}:`, logError);
   868â†’            // Don't fail the upload process for logging errors
   869â†’          }
   870â†’
   871â†’          // Create metadata record for successfully uploaded file
   872â†’          try {
   873â†’            await createMetadataRecord({
   874â†’              originalName: queueFile.name,
   875â†’              lastModified: queueFile.lastModified,
   876â†’              fileHash: fileHash,
   877â†’              sessionId: getCurrentSessionId(),
