   882â†’            // Don't fail the upload process for metadata errors
   883â†’          }
   884â†’        }
   885â†’      } catch (error) {
   886â†’        if (error.name === 'AbortError') {
   887â†’          console.log(`Upload aborted for: ${queueFile.name}`);
   888â†’          break;
   889â†’        }
   890â†’        console.error(`Failed to upload ${queueFile.name}:`, error);
   891â†’        updateUploadStatus('failed');
