341â†’      emit('clear-queue');
342â†’    } catch (emitError) {
343â†’    console.error('Failed to emit clear-queue event:', emitError);
344â†’    }
345â†’  }
346â†’};
347â†’
348â†’// Computed properties
349â†’const uploadableFiles = computed(() => {
350â†’  return props.files.filter((file) => !file.isDuplicate);
351â†’});
352â†’
353â†’const skippableFiles = computed(() => {
354â†’  return props.files.filter((file) => file.isDuplicate);
355â†’});
356â†’
357â†’// Group files for better duplicate visualization
358â†’const groupedFiles = computed(() => {
359â†’  const groups = new Map();
360â†’
361â†’  // Group files by hash (for duplicates) or by unique ID (for singles)
362â†’  props.files.forEach((file) => {
363â†’    const groupKey = file.hash || `unique_${file.name}_${file.size}_${file.lastModified}`;
364â†’
365â†’    if (!groups.has(groupKey)) {
366â†’      groups.set(groupKey, {
367â†’        files: [],
368â†’        isDuplicateGroup: false,
369â†’        groupName: file.name,
370â†’      });
371â†’    }
372â†’
373â†’    groups.get(groupKey).files.push(file);
374â†’  });
375â†’
376â†’  // Mark groups with multiple files as duplicate groups
377â†’  for (const group of groups.values()) {
378â†’    if (group.files.length > 1) {
379â†’      group.isDuplicateGroup = true;
380â†’      // Sort within group: kept files first, then duplicates
381â†’      group.files.sort((a, b) => {
382â†’        if (a.isDuplicate !== b.isDuplicate) {
383â†’          return a.isDuplicate ? 1 : -1; // Non-duplicates first
384â†’        }
385â†’        return a.originalIndex - b.originalIndex;
386â†’      });
387â†’    }
388â†’  }
389â†’
390â†’  return Array.from(groups.values());
391â†’});
392â†’
393â†’// Watch for file changes and populate existing hashes
394â†’watch(() => props.files, populateExistingHashes, { deep: true });
395â†’
396â†’const totalSize = computed(() => {
397â†’  return uploadableFiles.value.reduce((total, file) => total + file.size, 0);
398â†’});
399â†’
400â†’const hasErrors = computed(() => {
401â†’  return props.files.some((file) => file.status === 'error');
402â†’});
403â†’
404â†’const hasUploadStarted = computed(() => {
405â†’  return (
406â†’    props.isUploading ||
407â†’    props.isPaused ||
408â†’    props.isStartingUpload ||
409â†’    props.uploadStatus.successful > 0 ||
410â†’    props.uploadStatus.failed > 0 ||
411â†’    props.uploadStatus.skipped > 0
412â†’  );
413â†’});
414â†’
415â†’// Methods
416â†’const formatFileSize = (bytes) => {
417â†’  if (bytes === 0) return '0 B';
418â†’
419â†’  const k = 1024;
420â†’  const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
421â†’  const i = Math.floor(Math.log(bytes) / Math.log(k));
422â†’
423â†’  return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`;
424â†’};
425â†’
426â†’const getCurrentActionText = () => {
427â†’  switch (props.uploadStatus.currentAction) {
428â†’    case 'calculating_hash':
429â†’      return 'Calculating file hash...';
430â†’    case 'checking_existing':
431â†’      return 'Checking if file exists...';
432â†’    case 'uploading':
433â†’      return 'Uploading file...';
434â†’    default:
435â†’      return 'Processing file...';
436â†’  }
437â†’};
438â†’
439â†’// 2-chunk loading message generator
440â†’const getLoadingMessage = () => {
441â†’  if (!props.isProcessingUIUpdate) return 'Loading files into queue...';
442â†’
443â†’  const { total, percentage } = props.uiUpdateProgress;
444â†’
445â†’  if (total <= 100) {
446â†’    return 'Loading files into queue...';
447â†’  }
448â†’
449â†’  if (percentage <= 15) {
450â†’    return 'Showing initial files...';
451â†’  } else if (percentage < 100) {
452â†’    return 'Loading remaining files...';
453â†’  } else {
454â†’    return 'Complete!';
455â†’  }
456â†’};
457â†’
458â†’// Phase message for 2-chunk strategy
459â†’const getPhaseMessage = () => {
460â†’  if (!props.isProcessingUIUpdate) return 'Loading...';
461â†’
462â†’  const { total, percentage } = props.uiUpdateProgress;
463â†’
464â†’  if (total <= 100) {
465â†’    return 'Loading...';
466â†’  }
467â†’
468â†’  if (percentage <= 15) {
469â†’    return 'Step 1/2';
470â†’  } else if (percentage < 100) {
471â†’    return 'Step 2/2';
472â†’  } else {
473â†’    return 'Complete!';
474â†’  }
475â†’};
476â†’</script>
477â†’
478â†’<style scoped>
