191â†’import QueueTimeProgress from './QueueTimeProgress.vue';
192â†’import ClearAllButton from '../../base/ClearAllButton.vue';
193â†’
194â†’// Component configuration
195â†’defineOptions({
196â†’  name: 'FileUploadQueue',
197â†’});
198â†’
199â†’// Props
200â†’const props = defineProps({
201â†’  files: {
