208â†’    default: false,
209â†’  },
210â†’  uiUpdateProgress: {
211â†’    type: Object,
212â†’    default: () => ({
213â†’      current: 0,
214â†’      total: 0,
215â†’      percentage: 0,
216â†’      phase: 'loading',
217â†’    }),
218â†’  },
219â†’  // Time progress props
220â†’  showTimeProgress: {
221â†’    type: Boolean,
222â†’    default: false,
223â†’  },
224â†’  timeProgress: {
225â†’    type: Object,
226â†’    default: () => ({
227â†’      elapsedTime: 0,
228â†’      progressPercentage: 0,
229â†’      isOverdue: false,
230â†’      overdueSeconds: 0,
231â†’      timeRemaining: 0,
232â†’      formattedElapsedTime: '0s',
233â†’      formattedTimeRemaining: '0s',
234â†’      estimatedDuration: 0,
235â†’    }),
236â†’  },
237â†’  // Upload state props
238â†’  isUploading: {
239â†’    type: Boolean,
240â†’    default: false,
241â†’  },
242â†’  isPaused: {
243â†’    type: Boolean,
244â†’    default: false,
245â†’  },
246â†’  isStartingUpload: {
247â†’    type: Boolean,
248â†’    default: false,
249â†’  },
250â†’  totalAnalyzedFiles: {
251â†’    type: Number,
252â†’    default: null,
253â†’  },
254â†’  uploadStatus: {
255â†’    type: Object,
256â†’    default: () => ({
257â†’      successful: 0,
258â†’      failed: 0,
259â†’      skipped: 0,
260â†’      isUploading: false,
261â†’      currentFile: null,
262â†’      currentAction: null,
263â†’    }),
264â†’  },
265â†’});
266â†’
267â†’// Emits
268â†’const emit = defineEmits([
269â†’  'remove-file',
270â†’  'start-upload',
271â†’  'pause-upload',
272â†’  'resume-upload',
273â†’  'clear-queue',
274â†’]);
275â†’
276â†’// Hash tooltip functionality (only for cache management)
277â†’const { populateExistingHash, clearCache } = useLazyHashTooltip();
278â†’
279â†’// Populate existing hashes when component mounts or props change
280â†’const populateExistingHashes = () => {
281â†’  props.files.forEach((file) => {
282â†’    if (file.hash) {
283â†’      populateExistingHash(file.id || file.name, file.hash);
284â†’    }
285â†’  });
286â†’};
287â†’
288â†’// Populate hashes on mount and when files change
289â†’onMounted(populateExistingHashes);
290â†’
291â†’// Clean up on unmount
292â†’onUnmounted(() => {
293â†’  clearCache();
294â†’});
295â†’
296â†’// Lazy file list functionality
297â†’const { loadItem, isItemLoaded, preloadInitialItems, resetLoadedItems } = useLazyFileList(
298â†’  computed(() => groupedFiles.value)
299â†’);
300â†’
301â†’// Preload initial items for better UX
302â†’onMounted(() => {
303â†’  preloadInitialItems(10);
304â†’});
305â†’
306â†’// Reset loaded items when files change
307â†’watch(
308â†’  () => props.files,
309â†’  () => {
310â†’    resetLoadedItems();
311â†’    // Re-preload initial items after files change
312â†’    preloadInitialItems(10);
313â†’  },
314â†’  { deep: true }
315â†’);
316â†’
317â†’// Clear queue handler with UI component cleanup
318â†’const handleClearQueue = () => {
319â†’  try {
320â†’    console.log('Clearing UI component caches...');
321â†’
322â†’    // Clear lazy loading caches (idempotent operations)
323â†’    try {
324â†’      clearCache(); // Hash tooltip cache
325â†’    } catch (error) {
326â†’      console.warn('Error clearing hash tooltip cache:', error);
327â†’    }
328â†’
329â†’    try {
330â†’      resetLoadedItems(); // Lazy file list cache
331â†’    } catch (error) {
332â†’      console.warn('Error resetting loaded items:', error);
333â†’    }
334â†’
335â†’    // Always emit the clear-queue event to parent
336â†’    emit('clear-queue');
337â†’  } catch (error) {
338â†’    console.error('Error during UI component cleanup:', error);
339â†’    // Always emit the event even if cleanup fails
340â†’    try {
341â†’      emit('clear-queue');
342â†’    } catch (emitError) {
343â†’    console.error('Failed to emit clear-queue event:', emitError);
344â†’    }
345â†’  }
346â†’};
347â†’
348â†’// Computed properties
349â†’const uploadableFiles = computed(() => {
350â†’  return props.files.filter((file) => !file.isDuplicate);
351â†’});
352â†’
353â†’const skippableFiles = computed(() => {
354â†’  return props.files.filter((file) => file.isDuplicate);
355â†’});
356â†’
357â†’// Group files for better duplicate visualization
358â†’const groupedFiles = computed(() => {
359â†’  const groups = new Map();
360â†’
361â†’  // Group files by hash (for duplicates) or by unique ID (for singles)
362â†’  props.files.forEach((file) => {
363â†’    const groupKey = file.hash || `unique_${file.name}_${file.size}_${file.lastModified}`;
364â†’
365â†’    if (!groups.has(groupKey)) {
366â†’      groups.set(groupKey, {
367â†’        files: [],
368â†’        isDuplicateGroup: false,
369â†’        groupName: file.name,
370â†’      });
371â†’    }
372â†’
373â†’    groups.get(groupKey).files.push(file);
374â†’  });
375â†’
376â†’  // Mark groups with multiple files as duplicate groups
377â†’  for (const group of groups.values()) {
378â†’    if (group.files.length > 1) {
