   168â†’// File states (should match useUploadManager)
   169â†’const FILE_STATES = {
   170â†’  QUEUED: 'queued',
   171â†’  UPLOADING: 'uploading',
   172â†’  COMPLETED: 'completed',
   173â†’  FAILED: 'failed',
   174â†’  SKIPPED: 'skipped',
   175â†’  RETRYING: 'retrying',
   176â†’};
   177â†’
   178â†’// Computed properties for file state
   179â†’const isUploading = computed(() => props.fileState.state === FILE_STATES.UPLOADING);
   180â†’const isCompleted = computed(() => props.fileState.state === FILE_STATES.COMPLETED);
   181â†’const isFailed = computed(() => props.fileState.state === FILE_STATES.FAILED);
   182â†’const isSkipped = computed(() => props.fileState.state === FILE_STATES.SKIPPED);
   183â†’const isRetrying = computed(() => props.fileState.state === FILE_STATES.RETRYING);
   184â†’const isRetryable = computed(() => props.fileState.error?.classified?.retryable || false);
   185â†’
   186â†’// File type detection
   187â†’const getFileIcon = () => {
   188â†’  const extension = props.fileName.split('.').pop()?.toLowerCase() || '';
   189â†’
   190â†’  // Image files
   191â†’  if (['jpg', 'jpeg', 'png', 'gif', 'bmp', 'svg', 'webp'].includes(extension)) {
   192â†’    return 'mdi-image';
   193â†’  }
   194â†’
   195â†’  // Document files
   196â†’  if (['pdf', 'doc', 'docx', 'txt', 'rtf'].includes(extension)) {
   197â†’    return 'mdi-file-document';
   198â†’  }
   199â†’
   200â†’  // Spreadsheet files
