   253â†’};
   254â†’
   255â†’const getStatusColor = () => {
   256â†’  switch (props.fileState.state) {
   257â†’    case FILE_STATES.QUEUED:
   258â†’      return 'grey';
   259â†’    case FILE_STATES.UPLOADING:
   260â†’      return 'primary';
   261â†’    case FILE_STATES.COMPLETED:
   262â†’      return 'success';
   263â†’    case FILE_STATES.FAILED:
   264â†’      return 'error';
   265â†’    case FILE_STATES.SKIPPED:
   266â†’      return 'info';
   267â†’    case FILE_STATES.RETRYING:
   268â†’      return 'warning';
   269â†’    default:
   270â†’      return 'grey';
   271â†’  }
   272â†’};
   273â†’
   274â†’const getStatusText = () => {
   275â†’  switch (props.fileState.state) {
   276â†’    case FILE_STATES.QUEUED:
   277â†’      return 'Queued';
   278â†’    case FILE_STATES.UPLOADING:
   279â†’      return 'Uploading...';
   280â†’    case FILE_STATES.COMPLETED:
   281â†’      return 'Completed';
   282â†’    case FILE_STATES.FAILED:
   283â†’      return 'Failed';
   284â†’    case FILE_STATES.SKIPPED:
   285â†’      return 'Skipped (duplicate)';
   286â†’    case FILE_STATES.RETRYING:
   287â†’      return 'Retrying...';
   288â†’    default:
   289â†’      return 'Unknown';
   290â†’  }
   291â†’};
   292â†’
   293â†’const getStatusTextClass = () => {
   294â†’  switch (props.fileState.state) {
   295â†’    case FILE_STATES.COMPLETED:
   296â†’      return 'text-success';
   297â†’    case FILE_STATES.FAILED:
   298â†’      return 'text-error';
   299â†’    case FILE_STATES.SKIPPED:
   300â†’      return 'text-info';
   301â†’    case FILE_STATES.RETRYING:
   302â†’      return 'text-warning';
   303â†’    default:
   304â†’      return 'text-grey-darken-1';
   305â†’  }
   306â†’};
   307â†’
   308â†’// Error handling
   309â†’const getErrorTitle = () => {
   310â†’  const errorType = props.fileState.error?.classified?.type || 'unknown';
   311â†’
   312â†’  const titles = {
   313â†’    network: 'Network Error',
   314â†’    permission: 'Permission Denied',
   315â†’    storage: 'Storage Limit Exceeded',
   316â†’    file_size: 'File Too Large',
   317â†’    server: 'Server Error',
   318â†’    authentication: 'Authentication Error',
   319â†’    client: 'Upload Cancelled',
   320â†’    unknown: 'Upload Error',
   321â†’  };
   322â†’
   323â†’  return titles[errorType] || 'Upload Error';
   324â†’};
   325â†’
   326â†’const getErrorMessage = () => {
   327â†’  if (props.fileState.error?.classified?.message) {
   328â†’    return props.fileState.error.classified.message;
   329â†’  }
   330â†’
   331â†’  if (props.fileState.error?.message) {
   332â†’    return props.fileState.error.message;
   333â†’  }
   334â†’
   335â†’  return 'An unknown error occurred during upload';
   336â†’};
   337â†’
   338â†’// Utility functions
   339â†’const formatFileSize = (bytes) => {
   340â†’  if (bytes === 0) return '0 B';
   341â†’  const k = 1024;
   342â†’  const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
   343â†’  const i = Math.floor(Math.log(bytes) / Math.log(k));
   344â†’  return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`;
   345â†’};
   346â†’
   347â†’const formatSpeed = (bytesPerSecond) => {
   348â†’  if (bytesPerSecond < 1024) {
   349â†’    return `${bytesPerSecond.toFixed(0)} B/s`;
   350â†’  } else if (bytesPerSecond < 1024 * 1024) {
   351â†’    return `${(bytesPerSecond / 1024).toFixed(1)} KB/s`;
   352â†’  } else {
   353â†’    return `${(bytesPerSecond / (1024 * 1024)).toFixed(1)} MB/s`;
   354â†’  }
   355â†’};
