   376â†’        hardwarePerformanceFactor = 1.61;
   377â†’      }
   378â†’
   379â†’      // Create file analysis data for calibrated estimation
   380â†’      const sizeMap = new Map();
   381â†’      files.forEach((file) => {
   382â†’        const size = file.size || 0;
   383â†’        sizeMap.set(size, (sizeMap.get(size) || 0) + 1);
